        //    }
        //
        //    {
        //      table => 'order',
        //      data => array(
        //        {
        //          accountId => '39422',
        //          clientOrderId => '1652713431643',
        //          orderId => '1002215706472',
        //          quantity => '0.001',
        //          side => 'SELL',
        //          status => 'FILLED',
        //          marketCode => 'BTC-USD-SWAP-LIN',
        //          timestamp => '1652713431854',
        //          matchId => '304734619690202846', // trade field
        //          matchPrice => '29480.0', // trade field
