    public function parse_balance($response): array {
        $result = array( 'info' => $response );
        $balances = $this->omit($response, array(
            'errorCode',
            'result',
            'normalWallets',
        ));
        $currencyIds = is_array($balances) ? array_keys($balances) : array();
        for ($i = 0; $i < count($currencyIds); $i++) {
            $currencyId = $currencyIds[$i];
            $balance = $balances[$currencyId];
            $code = $this->safe_currency_code($currencyId);
            $account = $this->account();
            $account['free'] = $this->safe_string($balance, 'avail');
            $account['total'] = $this->safe_string($balance, 'balance');
            $result[$code] = $account;
        }
        return $this->safe_balance($result);
    }

    public function fetch_balance($params = array ()): array {
        /**
         * query for balance and get the amount of funds available for trading or funds locked in orders
         * @see https://docs.coinone.co.kr/v1.0/reference/v21
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a ~@link https://docs.ccxt.com/#/?id=balance-structure balance structure~
         */
        $this->load_markets();
        $response = $this->v2PrivatePostAccountBalance ($params);
        return $this->parse_balance($response);
    }

    public function fetch_order_book(string $symbol, ?int $limit = null, $params = array ()): array {
        /**
         * fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data
         * @see https://docs.coinone.co.kr/v1.0/reference/orderbook
         * @param {string} $symbol unified $symbol of the $market to fetch the order book for
         * @param {int} [$limit] the maximum amount of order book entries to return
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} A dictionary of ~@link https://docs.ccxt.com/#/?id=order-book-structure order book structures~ indexed by $market symbols
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'quote_currency' => $market['quote'],
            'target_currency' => $market['base'],
        );
        if ($limit !== null) {
            $request['size'] = $limit; // only support 5, 10, 15, 16
        }
        $response = $this->v2PublicGetOrderbookQuoteCurrencyTargetCurrency (array_merge($request, $params));
        //
        //     {
        //         "result" => "success",
        //         "error_code" => "0",
        //         "timestamp" => 1701071108673,
        //         "id" => "1701071108673001",
        //         "quote_currency" => "KRW",
        //         "target_currency" => "BTC",
        //         "order_book_unit" => "0.0",
        //         "bids" => array(
        //             {
        //                 "price" => "50048000",
        //                 "qty" => "0.01080229"
        //             }
        //         ),
        //         "asks" => array(
        //             {
        //                 "price" => "50058000",
        //                 "qty" => "0.00272592"
        //             }
        //         )
        //     }
        //
        $timestamp = $this->safe_integer($response, 'timestamp');
        return $this->parse_order_book($response, $market['symbol'], $timestamp, 'bids', 'asks', 'price', 'qty');
    }

