    public function parse_ticker($ticker, ?array $market = null): array {
        //
        //     {
        //         "quote_currency" => "krw",
        //         "target_currency" => "btc",
        //         "timestamp" => 1701073357818,
        //         "high" => "50543000.0",
        //         "low" => "49945000.0",
        //         "first" => "50487000.0",
        //         "last" => "50062000.0",
        //         "quote_volume" => "11349804285.3859",
        //         "target_volume" => "226.07268994",
        //         "best_asks" => array(
        //             {
        //                 "price" => "50081000.0",
        //                 "qty" => "0.18471358"
        //             }
        //         ),
        //         "best_bids" => array(
        //             {
        //                 "price" => "50062000.0",
        //                 "qty" => "0.04213455"
        //             }
        //         ),
        //         "id" => "1701073357818001"
        //     }
        //
        $timestamp = $this->safe_integer($ticker, 'timestamp');
        $last = $this->safe_string($ticker, 'last');
        $asks = $this->safe_value($ticker, 'best_asks');
        $bids = $this->safe_value($ticker, 'best_bids');
        $baseId = $this->safe_string($ticker, 'target_currency');
        $quoteId = $this->safe_string($ticker, 'quote_currency');
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        return $this->safe_ticker(array(
            'symbol' => $base . '/' . $quote,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'high' => $this->safe_string($ticker, 'high'),
            'low' => $this->safe_string($ticker, 'low'),
            'bid' => $this->safe_string($bids, 'price'),
            'bidVolume' => $this->safe_string($bids, 'qty'),
            'ask' => $this->safe_string($asks, 'price'),
            'askVolume' => $this->safe_string($asks, 'qty'),
            'vwap' => null,
            'open' => $this->safe_string($ticker, 'first'),
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => null,
            'percentage' => null,
            'average' => null,
            'baseVolume' => $this->safe_string($ticker, 'target_volume'),
            'quoteVolume' => $this->safe_string($ticker, 'quote_volume'),
            'info' => $ticker,
        ), $market);
    }

    public function parse_trade($trade, ?array $market = null): array {
        //
        // fetchTrades (public)
        //
        //     {
        //         "id" => "1701075265708001",
        //         "timestamp" => 1701075265708,
        //         "price" => "50020000",
        //         "qty" => "0.00155177",
        //         "is_seller_maker" => false
        //     }
        //
        // fetchMyTrades (private)
        //
        //     {
        //         "timestamp" => "1416561032",
        //         "price" => "419000.0",
        //         "type" => "bid",
        //         "qty" => "0.001",
        //         "feeRate" => "-0.0015",
        //         "fee" => "-0.0000015",
        //         "orderId" => "E84A1AC2-8088-4FA0-B093-A3BCDB9B3C85"
        //     }
        //
        $timestamp = $this->safe_integer($trade, 'timestamp');
        $market = $this->safe_market(null, $market);
        $isSellerMaker = $this->safe_value($trade, 'is_seller_maker');
        $side = null;
        if ($isSellerMaker !== null) {
            $side = $isSellerMaker ? 'sell' : 'buy';
        }
        $priceString = $this->safe_string($trade, 'price');
        $amountString = $this->safe_string($trade, 'qty');
        $orderId = $this->safe_string($trade, 'orderId');
        $feeCostString = $this->safe_string($trade, 'fee');
        $fee = null;
        if ($feeCostString !== null) {
            $feeCostString = Precise::string_abs($feeCostString);
            $feeRateString = $this->safe_string($trade, 'feeRate');
            $feeRateString = Precise::string_abs($feeRateString);
            $feeCurrencyCode = ($side === 'sell') ? $market['quote'] : $market['base'];
            $fee = array(
                'cost' => $feeCostString,
                'currency' => $feeCurrencyCode,
                'rate' => $feeRateString,
            );
        }
        return $this->safe_trade(array(
            'id' => $this->safe_string($trade, 'id'),
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'order' => $orderId,
            'symbol' => $market['symbol'],
            'type' => null,
            'side' => $side,
            'takerOrMaker' => null,
            'price' => $priceString,
            'amount' => $amountString,
            'cost' => null,
            'fee' => $fee,
        ), $market);
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * get the list of most recent trades for a particular $symbol
         * @see https://docs.coinone.co.kr/v1.0/reference/recent-completed-orders
         * @param {string} $symbol unified $symbol of the $market to fetch trades for
         * @param {int} [$since] timestamp in ms of the earliest trade to fetch
         * @param {int} [$limit] the maximum amount of trades to fetch
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {Trade[]} a list of ~@link https://docs.ccxt.com/#/?id=public-trades trade structures~
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'quote_currency' => $market['quote'],
            'target_currency' => $market['base'],
        );
        if ($limit !== null) {
            $request['size'] = min ($limit, 200);
        }
        $response = $this->v2PublicGetTradesQuoteCurrencyTargetCurrency (array_merge($request, $params));
        //
        //     {
        //         "result" => "success",
        //         "error_code" => "0",
        //         "server_time" => 1701075315771,
        //         "quote_currency" => "KRW",
        //         "target_currency" => "BTC",
        //         "transactions" => array(
        //             {
        //                 "id" => "1701075265708001",
        //                 "timestamp" => 1701075265708,
        //                 "price" => "50020000",
        //                 "qty" => "0.00155177",
        //                 "is_seller_maker" => false
        //             }
        //         )
        //     }
        //
        $data = $this->safe_value($response, 'transactions', array());
        return $this->parse_trades($data, $market, $since, $limit);
    }

    public function create_order(string $symbol, string $type, string $side, float $amount, ?float $price = null, $params = array ()) {
        /**
         * create a trade order
         * @see https://doc.coinone.co.kr/#tag/Order-V2/operation/v2_order_limit_buy
         * @see https://doc.coinone.co.kr/#tag/Order-V2/operation/v2_order_limit_sell
         * @param {string} $symbol unified $symbol of the $market to create an order in
         * @param {string} $type must be 'limit'
         * @param {string} $side 'buy' or 'sell'
         * @param {float} $amount how much of currency you want to trade in units of base currency
         * @param {float} [$price] the $price at which the order is to be fullfilled, in units of the quote currency, ignored in $market orders
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} an ~@link https://docs.ccxt.com/#/?id=order-structure order structure~
         */
        if ($type !== 'limit') {
            throw new ExchangeError($this->id . ' createOrder() allows limit orders only');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'price' => $price,
            'currency' => $market['id'],
            'qty' => $amount,
        );
        $method = 'privatePostOrder' . $this->capitalize($type) . $this->capitalize($side);
        $response = $this->$method (array_merge($request, $params));
        //
        //     {
        //         "result" => "success",
        //         "errorCode" => "0",
        //         "orderId" => "8a82c561-40b4-4cb3-9bc0-9ac9ffc1d63b"
        //     }
        //
        return $this->parse_order($response, $market);
    }

    public function fetch_order(string $id, ?string $symbol = null, $params = array ()) {
        /**
         * fetches information on an order made by the user
         * @param {string} $symbol unified $symbol of the $market the order was made in
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} An ~@link https://docs.ccxt.com/#/?$id=order-structure order structure~
         */
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' fetchOrder() requires a $symbol argument');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'order_id' => $id,
            'currency' => $market['id'],
        );
        $response = $this->v2PrivatePostOrderQueryOrder (array_merge($request, $params));
        //
        //     {
        //         "result" => "success",
        //         "errorCode" => "0",
        //         "orderId" => "0e3019f2-1e4d-11e9-9ec7-00e04c3600d7",
        //         "baseCurrency" => "KRW",
        //         "targetCurrency" => "BTC",
        //         "price" => "10011000.0",
        //         "originalQty" => "3.0",
        //         "executedQty" => "0.62",
        //         "canceledQty" => "1.125",
        //         "remainQty" => "1.255",
        //         "status" => "partially_filled",
        //         "side" => "bid",
        //         "orderedAt" => 1499340941,
        //         "updatedAt" => 1499341142,
        //         "feeRate" => "0.002",
