    public function fetch_currencies($params = array ()): ?array {
        /**
         * fetches all available currencies on an exchange
         *
         * @see https://doc.cryptomus.com/personal/market-cap/assets
         *
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} an associative dictionary of currencies
         */
        $response = $this->publicGetV1ExchangeMarketAssets ($params);
        //
        //     {
        //         'state' => '0',
        //         'result' => array(
        //             array(
        //                 'currency_code' => 'USDC',
        //                 'network_code' => 'bsc',
        //                 'can_withdraw' => true,
        //                 'can_deposit' => true,
        //                 'min_withdraw' => '1.00000000',
        //                 'max_withdraw' => '10000000.00000000',
        //                 'max_deposit' => '10000000.00000000',
        //                 'min_deposit' => '1.00000000'
        //             ),
        //             ...
        //         )
        //     }
        //
        $coins = $this->safe_list($response, 'result');
        $groupedById = $this->group_by($coins, 'currency_code');
        $keys = is_array($groupedById) ? array_keys($groupedById) : array();
        $result = array();
        for ($i = 0; $i < count($keys); $i++) {
            $id = $keys[$i];
            $code = $this->safe_currency_code($id);
            $networks = array();
            $networkEntries = $groupedById[$id];
            for ($j = 0; $j < count($networkEntries); $j++) {
                $networkEntry = $networkEntries[$j];
                $networkId = $this->safe_string($networkEntry, 'network_code');
                $networkCode = $this->network_id_to_code($networkId);
                $networks[$networkCode] = array(
                    'id' => $networkId,
                    'network' => $networkCode,
                    'limits' => array(
                        'withdraw' => array(
                            'min' => $this->safe_number($networkEntry, 'min_withdraw'),
                            'max' => $this->safe_number($networkEntry, 'max_withdraw'),
                        ),
                        'deposit' => array(
                            'min' => $this->safe_number($networkEntry, 'min_deposit'),
                            'max' => $this->safe_number($networkEntry, 'max_deposit'),
                        ),
                    ),
                    'active' => null,
                    'deposit' => $this->safe_bool($networkEntry, 'can_withdraw'),
                    'withdraw' => $this->safe_bool($networkEntry, 'can_deposit'),
                    'fee' => null,
                    'precision' => null,
                    'info' => $networkEntry,
                );
            }
            $result[$code] = $this->safe_currency_structure(array(
                'id' => $id,
                'code' => $code,
                'networks' => $networks,
                'info' => $networkEntries,
            ));
        }
        return $result;
    }

    public function fetch_tickers(?array $symbols = null, $params = array ()): array {
        /**
         * fetches price tickers for multiple markets, statistical information calculated over the past 24 hours for each market
         *
         * @see https://doc.cryptomus.com/personal/market-cap/tickers
         *
         * @param {string[]} [$symbols] unified $symbols of the markets to fetch the ticker for, all market tickers are returned if not assigned
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a dictionary of ~@link https://docs.ccxt.com/?id=ticker-structure ticker structures~
         */
        $this->load_markets();
        $symbols = $this->market_symbols($symbols);
        $response = $this->publicGetV1ExchangeMarketTickers ($params);
        //
        //     {
        //         "data" => [
        //         array(
        //             "currency_pair" => "MATIC_USDT",
        //             "last_price" => "0.342",
        //             "base_volume" => "1676.84092771",
        //             "quote_volume" => "573.48033609043"
        //         ),
        //         ...
        //     }
        //
        $data = $this->safe_list($response, 'data');
        return $this->parse_tickers($data, $symbols);
    }

    public function parse_ticker($ticker, ?array $market = null): array {
        //
        //     {
        //         "currency_pair" => "XMR_USDT",
        //         "last_price" => "158.04829772",
        //         "base_volume" => "0.35185785",
        //         "quote_volume" => "55.523761128544"
        //     }
        //
        $marketId = $this->safe_string($ticker, 'currency_pair');
        $market = $this->safe_market($marketId, $market);
        $symbol = $market['symbol'];
        $last = $this->safe_string($ticker, 'last_price');
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => null,
            'datetime' => null,
            'high' => null,
            'low' => null,
            'bid' => null,
            'bidVolume' => null,
            'ask' => null,
            'askVolume' => null,
            'vwap' => null,
            'open' => null,
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => null,
            'percentage' => null,
            'average' => null,
            'baseVolume' => $this->safe_string($ticker, 'base_volume'),
            'quoteVolume' => $this->safe_string($ticker, 'quote_volume'),
            'info' => $ticker,
        ), $market);
    }

    public function fetch_order_book(string $symbol, ?int $limit = null, $params = array ()): array {
        /**
         * fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other $data
         *
         * @see https://doc.cryptomus.com/personal/market-cap/orderbook
         *
         * @param {string} $symbol unified $symbol of the $market to fetch the order book for
         * @param {int} [$limit] the maximum amount of order book entries to return
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {int} [$params->level] 0 or 1 or 2 or 3 or 4 or 5 - the $level of volume
         * @return {array} A dictionary of ~@link https://docs.ccxt.com/?id=order-book-structure order book structures~ indexed by $market symbols
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'currencyPair' => $market['id'],
        );
        $level = 0;
        list($level, $params) = $this->handle_option_and_params($params, 'fetchOrderBook', 'level', $level);
        $request['level'] = $level;
        $response = $this->publicGetV1ExchangeMarketOrderBookCurrencyPair ($this->extend($request, $params));
        //
        //     {
        //         "data" => {
        //             "timestamp" => "1730138702",
        //             "bids" => array(
        //                 {
        //                     "price" => "2250.00",
        //                     "quantity" => "1.00000"
        //                 }
        //             ),
        //             "asks" => array(
        //                 {
        //                     "price" => "2428.69",
        //                     "quantity" => "0.16470"
        //                 }
        //             )
        //         }
        //     }
        //
        $data = $this->safe_dict($response, 'data', array());
        $timestamp = $this->safe_timestamp($data, 'timestamp');
        return $this->parse_order_book($data, $symbol, $timestamp, 'bids', 'asks', 'price', 'quantity');
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * get the list of most recent trades for a particular $symbol
         *
         * @see https://doc.cryptomus.com/personal/market-cap/trades
         *
         * @param {string} $symbol unified $symbol of the $market to fetch trades for
         * @param {int} [$since] timestamp in ms of the earliest trade to fetch
         * @param {int} [$limit] the maximum amount of trades to fetch (maximum value is 100)
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {Trade[]} a list of ~@link https://docs.ccxt.com/?id=public-trades trade structures~
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $request = array(
            'currencyPair' => $market['id'],
        );
        $response = $this->publicGetV1ExchangeMarketTradesCurrencyPair ($this->extend($request, $params));
        //
        //     {
        //         "data" => array(
        //             {
        //                 "trade_id" => "01J829C3RAXHXHR09HABGQ1YAT",
        //                 "price" => "2315.6320500000000000",
