        for ($i = 0; $i < count($this->symbols); $i++) {
            $symbol = $this->symbols[$i];
            $result[$symbol] = array(
                'info' => $response,
                'symbol' => $symbol,
                'maker' => $this->parse_number($makerFee),
                'taker' => $this->parse_number($takerFee),
                'percentage' => true,
                'tierBased' => true,
                'tiers' => $tiers,
            );
        }
        return $result;
    }

    public function parse_fee_tiers($feeTiers, ?array $market = null) {
        $takerFees = array();
        $makerFees = array();
        for ($i = 0; $i < count($feeTiers); $i++) {
            $tier = $feeTiers[$i];
            $turnover = $this->safe_number($tier, 'from_turnover');
            $taker = $this->safe_string($tier, 'taker_percent');
            $maker = $this->safe_string($tier, 'maker_percent');
            $maker = Precise::string_div($maker, '100');
            $taker = Precise::string_div($taker, '100');
            $makerFees[] = array( $turnover, $this->parse_number($maker) );
            $takerFees[] = array( $turnover, $this->parse_number($taker) );
        }
        return array(
            'maker' => $makerFees,
            'taker' => $takerFees,
        );
    }

    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $endpoint = $this->implode_params($path, $params);
        $params = $this->omit($params, $this->extract_params($path));
        $url = $this->urls['api'][$api] . '/' . $endpoint;
        if ($api === 'private') {
            $this->check_required_credentials();
            $jsonParams = '';
            $headers = array(
                'userId' => $this->uid,
            );
            if ($method !== 'GET') {
                $body = $this->json($params);
                $jsonParams = $body;
                $headers['Content-Type'] = 'application/json';
            } else {
                $query = $this->urlencode($params);
                if (strlen($query) !== 0) {
                    $url .= '?' . $query;
                }
            }
            $jsonParamsBase64 = base64_encode($jsonParams);
            $stringToSign = $jsonParamsBase64 . $this->secret;
            $signature = $this->hash($this->encode($stringToSign), 'md5');
            $headers['sign'] = $signature;
        } else {
            $query = $this->urlencode($params);
            if (strlen($query) !== 0) {
                $url .= '?' . $query;
            }
        }
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function handle_errors(int $httpCode, string $reason, string $url, string $method, array $headers, string $body, $response, $requestHeaders, $requestBody) {
        if ($response === null) {
            return null;
        }
        if (is_array($response) && array_key_exists('code', $response)) {
            $code = $this->safe_string($response, 'code');
            $feedback = $this->id . ' ' . $body;
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $code, $feedback);
            throw new ExchangeError($feedback);
        } elseif (is_array($response) && array_key_exists('message', $response)) {
            //
            //      array("message":"Minimum amount 15 USDT","state":1)
            //
            $message = $this->safe_string($response, 'message');
            $feedback = $this->id . ' ' . $body;
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $message, $feedback);
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $message, $feedback);
            throw new ExchangeError($feedback); // unknown $message
        }
        return null;
    }
