    public function parse_market(array $market): array {
        $type = $this->safe_string($market, 'instrument_type');
        $spot = false;
        $margin = true;
        $swap = false;
        $option = false;
        $linear = null;
        $baseId = $this->safe_string($market, 'base_currency');
        $quoteId = $this->safe_string($market, 'quote_currency');
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $marketId = $this->safe_string($market, 'instrument_name');
        $symbol = $base . '/' . $quote;
        $settleId = null;
        $settle = null;
        $expiry = null;
        $strike = null;
        $optionType = null;
        $optionLetter = null;
        if ($type === 'erc20') {
            $spot = true;
            $marketType = 'spot';
        } elseif ($type === 'perp') {
            $margin = false;
            $settleId = 'USDC';
            $settle = $this->safe_currency_code($settleId);
            $symbol = $base . '/' . $quote . ':' . $settle;
            $swap = true;
            $linear = true;
            $marketType = 'swap';
        } elseif ($type === 'option') {
            $settleId = 'USDC';
            $settle = $this->safe_currency_code($settleId);
            $margin = false;
            $option = true;
            $marketType = 'option';
            $optionDetails = $this->safe_dict($market, 'option_details');
            $expiry = $this->safe_timestamp($optionDetails, 'expiry');
            $strike = $this->safe_integer($optionDetails, 'strike');
            $optionLetter = $this->safe_string($optionDetails, 'option_type');
            $symbol = $base . '/' . $quote . ':' . $settle . '-' . $this->yymmdd($expiry) . '-' . $this->number_to_string($strike) . '-' . $optionLetter;
            if ($optionLetter === 'P') {
                $optionType = 'put';
            } else {
                $optionType = 'call';
            }
        }
        return $this->safe_market_structure(array(
            'id' => $marketId,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'settle' => $settle,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'settleId' => $settleId,
            'type' => $marketType,
            'spot' => $spot,
            'margin' => $margin,
            'swap' => $swap,
            'future' => false,
            'option' => $option,
            'active' => $this->safe_bool($market, 'is_active'),
            'contract' => ($swap || $option),
            'linear' => $linear,
            'inverse' => null,
            'contractSize' => ($spot) ? null : 1,
            'expiry' => $expiry,
            'expiryDatetime' => $this->iso8601($expiry),
            'taker' => $this->safe_number($market, 'taker_fee_rate'),
            'maker' => $this->safe_number($market, 'maker_fee_rate'),
            'strike' => $strike,
            'optionType' => $optionType,
            'precision' => array(
                'amount' => $this->safe_number($market, 'amount_step'),
                'price' => $this->safe_number($market, 'tick_size'),
            ),
            'limits' => array(
                'leverage' => array(
                    'min' => null,
                    'max' => null,
                ),
                'amount' => array(
                    'min' => $this->safe_number($market, 'minimum_amount'),
                    'max' => $this->safe_number($market, 'maximum_amount'),
                ),
                'price' => array(
                    'min' => null,
                    'max' => null,
                ),
                'cost' => array(
                    'min' => null,
                    'max' => null,
                ),
            ),
            'created' => null,
            'info' => $market,
        ));
    }

