        }
        $maxFeeString = $this->number_to_string($maxFee);
        $amountString = $this->number_to_string($amount);
        $tradeModuleDataHash = $this->hash($this->eth_abi_encode(array(
            'address', 'uint', 'int', 'int', 'uint', 'uint', 'bool',
