            }
        }
        if ($postOnly !== null) {
            $request['time_in_force'] = 'post_only';
        } elseif ($timeInForce !== null) {
            $request['time_in_force'] = $timeInForce;
        }
        $stopLoss = $this->safe_value($params, 'stopLoss');
        $takeProfit = $this->safe_value($params, 'takeProfit');
