                $subResult[$baseCode] = $this->fetch_balance_helper($base);
                $subResult[$quoteCode] = $this->fetch_balance_helper($quote);
                $result[$symbol] = $this->safe_balance($subResult);
            } else {
                $code = $this->safe_currency_code($this->safe_string($entry, 'currency'));
                $result[$code] = $this->fetch_balance_helper($entry);
            }
        }
        return ($margin && !$crossMargin) ? $result : $this->safe_balance($result);
    }

    public function fetch_ohlcv($symbol, $timeframe = '1m', $since = null, $limit = null, $params = array ()) {
        /**
         * fetches historical candlestick data containing the open, high, low, and close $price, and the volume of a $market
         * @param {string} $symbol unified $symbol of the $market $to fetch OHLCV data for
         * @param {string} $timeframe the length of time each candle represents
         * @param {int|null} $since timestamp in ms of the earliest candle $to fetch
         * @param {int|null} $limit the maximum amount of candles $to fetch
         * @param {array} $params extra parameters specific $to the gateio api endpoint
         * @param {string|null} $params->price "mark" or "index" for mark $price and index $price candles
         * @param {int|null} $params->until timestamp in ms of the latest candle $to fetch
         * @return {[[int]]} A list of candles ordered as timestamp, open, high, low, close, volume (is_array(quote currency) && array_key_exists(units, quote currency))
         */
        $this->load_markets();
        $market = $this->market($symbol);
        $price = $this->safe_string($params, 'price');
        $request = array();
        list($request, $params) = $this->prepare_request($market, null, $params);
        $request['interval'] = $this->timeframes[$timeframe];
        $method = 'publicSpotGetCandlesticks';
        $maxLimit = 1000;
        if ($market['contract']) {
            $maxLimit = 1999;
            $limit = ($limit === null) ? $maxLimit : min ($limit, $maxLimit);
            if ($market['future']) {
                $method = 'publicDeliveryGetSettleCandlesticks';
            } elseif ($market['swap']) {
                $method = 'publicFuturesGetSettleCandlesticks';
            }
            $isMark = ($price === 'mark');
            $isIndex = ($price === 'index');
            if ($isMark || $isIndex) {
                $request['contract'] = $price . '_' . $market['id'];
                $params = $this->omit($params, 'price');
            }
        }
        $limit = ($limit === null) ? $maxLimit : min ($limit, $maxLimit);
        $until = $this->safe_integer($params, 'until');
        if ($until !== null) {
            $until = intval($until / 1000);
            $params = $this->omit($params, 'until');
        }
        if ($since !== null) {
            $duration = $this->parse_timeframe($timeframe);
            $request['from'] = intval($since / 1000);
            $toTimestamp = $this->sum($request['from'], $limit * $duration - 1);
            $currentTimestamp = $this->seconds();
            $to = min ($toTimestamp, $currentTimestamp);
            if ($until !== null) {
                $request['to'] = min ($to, $until);
            } else {
                $request['to'] = $to;
            }
        } else {
            if ($until !== null) {
                $request['to'] = $until;
            }
            $request['limit'] = $limit;
        }
        $response = $this->$method (array_merge($request, $params));
        return $this->parse_ohlcvs($response, $market, $timeframe, $since, $limit);
    }

    public function fetch_funding_rate_history($symbol = null, $since = null, $limit = null, $params = array ()) {
        /**
         * fetches historical funding rate prices
         * @param {string|null} $symbol unified $symbol of the $market to fetch the funding rate history for
         * @param {int|null} $since $timestamp in ms of the earliest funding rate to fetch
         * @param {int|null} $limit the maximum amount of ~@link https://docs.ccxt.com/en/latest/manual.html?#funding-rate-history-structure funding rate structures~ to fetch
         * @param {array} $params extra parameters specific to the gate api endpoint
         * @return {[array]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#funding-rate-history-structure funding rate structures~
         */
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' fetchFundingRateHistory() requires a $symbol argument');
        }
        $this->load_markets();
        $market = $this->market($symbol);
        if (!$market['swap']) {
            throw new BadSymbol($this->id . ' fetchFundingRateHistory() supports swap contracts only');
        }
        list($request, $query) = $this->prepare_request($market, null, $params);
        if ($limit !== null) {
            $request['limit'] = $limit;
        }
        $method = 'publicFuturesGetSettleFundingRate';
        $response = $this->$method (array_merge($request, $query));
        //
        //     {
        //         "r" => "0.00063521",
        //         "t" => "1621267200000",
        //     }
        //
        $rates = array();
        for ($i = 0; $i < count($response); $i++) {
            $entry = $response[$i];
            $timestamp = $this->safe_timestamp($entry, 't');
            $rates[] = array(
                'info' => $entry,
                'symbol' => $symbol,
                'fundingRate' => $this->safe_number($entry, 'r'),
                'timestamp' => $timestamp,
                'datetime' => $this->iso8601($timestamp),
            );
        }
        $sorted = $this->sort_by($rates, 'timestamp');
        return $this->filter_by_symbol_since_limit($sorted, $market['symbol'], $since, $limit);
    }

    public function parse_ohlcv($ohlcv, $market = null) {
        //
        // Spot $market candles
        //
        //    array(
        //        "1660957920", // timestamp
        //        "6227.070147198573", // quote volume
        //        "0.0000133485", // close
        //        "0.0000133615", // high
        //        "0.0000133347", // low
        //        "0.0000133468", // open
        //        "466641934.99" // base volume
        //    )
        //
        //
        // Mark and Index price candles
        //
        //     {
        //          "t":1632873600,         // Unix timestamp in seconds
        //          "o" => "41025",           // Open price
        //          "h" => "41882.17",        // Highest price
        //          "c" => "41776.92",        // Close price
        //          "l" => "40783.94"         // Lowest price
        //     }
        //
        if (gettype($ohlcv) === 'array' && array_keys($ohlcv) === array_keys(array_keys($ohlcv))) {
            return array(
                $this->safe_timestamp($ohlcv, 0),   // unix timestamp in seconds
                $this->safe_number($ohlcv, 5),      // open price
                $this->safe_number($ohlcv, 3),      // highest price
                $this->safe_number($ohlcv, 4),      // lowest price
                $this->safe_number($ohlcv, 2),      // close price
                $this->safe_number($ohlcv, 6),      // trading volume
            );
        } else {
            // Mark and Index price candles
            return array(
                $this->safe_timestamp($ohlcv, 't'), // unix timestamp in seconds
                $this->safe_number($ohlcv, 'o'),    // open price
                $this->safe_number($ohlcv, 'h'),    // highest price
                $this->safe_number($ohlcv, 'l'),    // lowest price
                $this->safe_number($ohlcv, 'c'),    // close price
                $this->safe_number($ohlcv, 'v'),    // trading volume, null for mark or index price
            );
        }
    }

