    public function fetch_markets($params = array ()) {
        /**
         * retrieves data on all markets for gemini
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array[]} an array of objects representing market data
         */
        $method = $this->safe_value($this->options, 'fetchMarketsMethod', 'fetch_markets_from_api');
        if ($method === 'fetch_markets_from_web') {
            $usdMarkets = $this->fetch_markets_from_web($params); // get usd markets
            $usdtMarkets = $this->fetch_usdt_markets($params); // get usdt markets
            return $this->array_concat($usdMarkets, $usdtMarkets);
        }
        return $this->fetch_markets_from_api($params);
    }

    public function fetch_markets_from_web($params = array ()) {
        $data = $this->fetch_web_endpoint('fetchMarkets', 'webGetRestApi', false, '<h1 id="symbols-and-minimums">Symbols and minimums</h1>');
        $error = $this->id . ' fetchMarketsFromWeb() the API doc HTML markup has changed, breaking the parser of order limits and precision info for markets.';
        $tables = explode('tbody>', $data);
        $numTables = count($tables);
        if ($numTables < 2) {
            throw new NotSupported($error);
        }
        $rows = explode("\n<tr>\n", $tables[1]); // eslint-disable-line quotes
        $numRows = count($rows);
        if ($numRows < 2) {
            throw new NotSupported($error);
        }
        $result = array();
        // skip the first element (empty string)
        for ($i = 1; $i < $numRows; $i++) {
            $row = $rows[$i];
            $cells = explode("</td>\n", $row); // eslint-disable-line quotes
            $numCells = count($cells);
            if ($numCells < 5) {
                throw new NotSupported($error);
            }
            //     array(
            //         '<td>btcusd', // currency
            //         '<td>0.00001 BTC (1e-5)', // min order size
            //         '<td>0.00000001 BTC (1e-8)', // tick size
            //         '<td>0.01 USD', // $quote currency price increment
            //         '</tr>'
            //     )
            $marketId = str_replace('<td>', '', $cells[0]);
            // $base = $this->safe_currency_code($baseId);
            $minAmountString = str_replace('<td>', '', $cells[1]);
            $minAmountParts = explode(' ', $minAmountString);
            $minAmount = $this->safe_number($minAmountParts, 0);
            $amountPrecisionString = str_replace('<td>', '', $cells[2]);
            $amountPrecisionParts = explode(' ', $amountPrecisionString);
            $idLength = strlen($marketId) - 0;
            $startingIndex = $idLength - 3;
            $pricePrecisionString = str_replace('<td>', '', $cells[3]);
            $pricePrecisionParts = explode(' ', $pricePrecisionString);
            $quoteId = $this->safe_string_lower($pricePrecisionParts, 1, mb_substr($marketId, $startingIndex, $idLength - $startingIndex));
            $baseId = $this->safe_string_lower($amountPrecisionParts, 1, str_replace($quoteId, '', $marketId));
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $result[] = array(
                'id' => $marketId,
                'symbol' => $base . '/' . $quote,
                'base' => $base,
                'quote' => $quote,
                'settle' => null,
                'baseId' => $baseId,
                'quoteId' => $quoteId,
                'settleId' => null,
                'type' => 'spot',
                'spot' => true,
                'margin' => false,
                'swap' => false,
                'future' => false,
                'option' => false,
                'active' => null,
                'contract' => false,
                'linear' => null,
                'inverse' => null,
                'contractSize' => null,
                'expiry' => null,
                'expiryDatetime' => null,
                'strike' => null,
                'optionType' => null,
                'precision' => array(
                    'amount' => $this->safe_number($amountPrecisionParts, 0),
                    'price' => $this->safe_number($pricePrecisionParts, 0),
                ),
                'limits' => array(
                    'leverage' => array(
                        'min' => null,
                        'max' => null,
                    ),
                    'amount' => array(
                        'min' => $minAmount,
                        'max' => null,
                    ),
                    'price' => array(
                        'min' => null,
                        'max' => null,
                    ),
                    'cost' => array(
                        'min' => null,
                        'max' => null,
                    ),
                ),
                'created' => null,
                'info' => $row,
            );
        }
        return $result;
    }

    public function parse_market_active($status) {
        $statuses = array(
            'open' => true,
            'closed' => false,
            'cancel_only' => true,
            'post_only' => true,
            'limit_only' => true,
        );
        return $this->safe_value($statuses, $status, true);
    }

    public function fetch_usdt_markets($params = array ()) {
        // these markets can't be scrapped and fetchMarketsFrom api does an extra call
        // to load market ids which we don't need here
        if (is_array($this->urls) && array_key_exists('test', $this->urls)) {
            return array(); // sandbox does not have usdt markets
        }
        $fetchUsdtMarkets = $this->safe_value($this->options, 'fetchUsdtMarkets', array());
        $result = array();
        for ($i = 0; $i < count($fetchUsdtMarkets); $i++) {
            $marketId = $fetchUsdtMarkets[$i];
            $request = array(
                'symbol' => $marketId,
            );
            // don't use Promise.all here, for some reason the exchange can't handle it and crashes
            $rawResponse = $this->publicGetV1SymbolsDetailsSymbol (array_merge($request, $params));
            $result[] = $this->parse_market($rawResponse);
        }
        return $result;
    }

    public function fetch_markets_from_api($params = array ()) {
        $response = $this->publicGetV1Symbols ($params);
        //
        //     array(
        //         "btcusd",
        //         "linkusd",
        //         ...
        //     )
        //
        $result = array();
        for ($i = 0; $i < count($response); $i++) {
            $marketId = $response[$i];
            $market = array(
                'symbol' => $marketId,
            );
            $result[$marketId] = $this->parse_market($market);
        }
        $options = $this->safe_value($this->options, 'fetchMarketsFromAPI', array());
        $fetchDetailsForAllSymbols = $this->safe_value($options, 'fetchDetailsForAllSymbols', false);
        $fetchDetailsForMarketIds = $this->safe_value($options, 'fetchDetailsForMarketIds', array());
        $promises = array();
        $marketIds = array();
        if ($fetchDetailsForAllSymbols) {
            $marketIds = $response;
        } else {
            $marketIds = $fetchDetailsForMarketIds;
        }
        for ($i = 0; $i < count($marketIds); $i++) {
            $marketId = $marketIds[$i];
            $request = array(
                'symbol' => $marketId,
            );
            $promises[] = $this->publicGetV1SymbolsDetailsSymbol (array_merge($request, $params));
            //
            //     {
            //         "symbol" => "BTCUSD",
            //         "base_currency" => "BTC",
            //         "quote_currency" => "USD",
            //         "tick_size" => 1E-8,
            //         "quote_increment" => 0.01,
            //         "min_order_size" => "0.00001",
            //         "status" => "open",
            //         "wrap_enabled" => false
            //     }
            //
        }
        $promises = $promises;
        for ($i = 0; $i < count($promises); $i++) {
            $responseInner = $promises[$i];
            $marketId = $this->safe_string_lower($responseInner, 'symbol');
            $result[$marketId] = $this->parse_market($responseInner);
        }
        return $this->to_array($result);
    }

    public function parse_market($response): array {
        $marketId = $this->safe_string_lower($response, 'symbol');
        $baseId = $this->safe_string($response, 'base_currency');
        $quoteId = $this->safe_string($response, 'quote_currency');
        if ($baseId === null) {
            $idLength = strlen($marketId) - 0;
            $isUSDT = mb_strpos($marketId, 'usdt') !== false;
            $quoteSize = $isUSDT ? 4 : 3;
            $baseId = mb_substr($marketId, 0, $idLength - $quoteSize - 0); // Not true for all markets
            $quoteId = mb_substr($marketId, $idLength - $quoteSize, $idLength - $idLength - $quoteSize);
        }
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $status = $this->safe_string($response, 'status');
        return array(
            'id' => $marketId,
            'symbol' => $base . '/' . $quote,
