        $request = array(
            'symbol' => $market['id'],
        );
        if ($limit !== null) {
            $request['limit_trades'] = min ($limit, 500);
        }
        if ($since !== null) {
            $request['timestamp'] = $since;
        }
        $response = $this->publicGetV1TradesSymbol (array_merge($request, $params));
        //
        //     array(
