    public function fetch_markets($params = array ()) {
        $response = $this->publicGetTradingPairs ($params);
        //
        //     array(
        //         array(
        //             "$id":1,
        //             "name":"ETH-KRW",
        //             "baseAsset":"ETH",
        //             "quoteAsset":"KRW",
        //             "baseAssetScale":8,
        //             "quoteAssetScale":0,
        //             "priceMin":1,
        //             "restApiOrderAmountMin":array(
        //                 "limitAsk":array("amount":10000,"unit":"KRW"),
        //                 "limitBid":array("amount":10000,"unit":"KRW"),
        //                 "$marketAsk":array("amount":0.001,"unit":"ETH"),
        //                 "$marketBid":array("amount":10000,"unit":"KRW"),
        //             ),
        //             "makerFeePercent":0.2,
        //             "takerFeePercent":0.2,
        //         ),
        //     )
        //
        $result = array();
        for ($i = 0; $i < count($response); $i++) {
            $market = $response[$i];
            $id = $this->safe_string($market, 'name');
            $numericId = $this->safe_integer($market, 'id');
            $baseId = $this->safe_string($market, 'baseAsset');
            $quoteId = $this->safe_string($market, 'quoteAsset');
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $symbol = $base . '/' . $quote;
            $precision = array(
                'price' => $this->safe_integer($market, 'quoteAssetScale'),
                'amount' => $this->safe_integer($market, 'baseAssetScale'),
            );
            $minimums = $this->safe_value($market, 'restApiOrderAmountMin', array());
            $marketAsk = $this->safe_value($minimums, 'marketAsk', array());
            $marketBid = $this->safe_value($minimums, 'marketBid', array());
            $takerFeePercentString = $this->safe_string($market, 'takerFeePercent');
            $makerFeePercentString = $this->safe_string($market, 'makerFeePercent');
            $taker = $this->parse_number(Precise::string_div($takerFeePercentString, '100'));
            $maker = $this->parse_number(Precise::string_div($makerFeePercentString, '100'));
            $result[] = array(
                'id' => $id,
                'info' => $market,
                'numericId' => $numericId,
                'symbol' => $symbol,
                'base' => $base,
                'quote' => $quote,
                'baseId' => $this->safe_string($market, 'baseAsset'),
                'quoteId' => $this->safe_string($market, 'quoteAsset'),
                'active' => true,
                'taker' => $taker,
                'maker' => $maker,
                'precision' => $precision,
                'limits' => array(
                    'amount' => array(
                        'min' => $this->safe_number($marketAsk, 'amount'),
                        'max' => null,
                    ),
                    'price' => array(
                        'min' => $this->safe_number($market, 'priceMin'),
                        'max' => null,
                    ),
                    'cost' => array(
                        'min' => $this->safe_number($marketBid, 'amount'),
                        'max' => null,
                    ),
                ),
            );
        }
        return $result;
    }

