        //         "clientOrderId" => "1722006209978370",
        //         "orderId" => "1738708541676585728",
        //         "transactTime" => "1722006209989",
        //         "price" => "5000",
        //         "origQty" => "0.005",
        //         "executedQty" => "0",
        //         "status" => "NEW",
        //         "timeInForce" => "GTC",
        //         "type" => "LIMIT_MAKER",
        //         "side" => "SELL"
        //     }
        //
        // createOrder swap
        //     {
        //         "time" => "1722429951611",
        //         "updateTime" => "1722429951648",
        //         "orderId" => "1742263144028363776",
        //         "clientOrderId" => "1722429950315",
