        //         "leverage" => "5",
        //         "origQty" => "10",
        //         "executedQty" => "10",
        //         "avgPrice" => "0",
        //         "marginLocked" => "6.9212",
        //         "type" => "LIMIT",
        //         "side" => "BUY_OPEN",
        //         "timeInForce" => "IOC",
        //         "status" => "FILLED",
        //         "priceType" => "MARKET",
        //         "contractMultiplier" => "0.00100000"
        //     }
        //
        // fetchOrder swap
        //     {
        //         "time" => "1722429951611",
