    public function parse_market($market, $type = 'spot') {
        $filters = $this->safe_value($market, 'filters', array());
        $id = $this->safe_string($market, 'symbol');
        $baseId = $this->safe_string($market, 'baseAsset');
        $quoteId = $this->safe_string($market, 'quoteAsset');
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $symbol = $base . '/' . $quote;
        $spot = true;
        $future = false;
        $option = false;
        $inverse = false;
        if ($type === 'future') {
            $symbol = $id;
            $spot = false;
            $future = true;
            $inverse = $this->safe_value($market, 'inverse', false);
            $baseId = $this->safe_string($market, 'underlying');
            $base = $this->safe_currency_code($baseId);
        } else if ($type === 'option') {
            $symbol = $id;
            $spot = false;
            $option = true;
        }
        $margin = $this->safe_value($market, 'allowMargin', null);
        $isAggregate = $this->safe_value($market, 'isAggregate', null);
        $active = true;
        if ($isAggregate === true) {
            $active = false;
        }
        $amountMin = null;
        $priceMin = null;
        $priceMax = null;
        $costMin = null;
        $pricePrecision = null;
        $amountPrecision = null;
        for ($j = 0; $j < count($filters); $j++) {
            $filter = $filters[$j];
            $filterType = $this->safe_string($filter, 'filterType');
            if ($filterType === 'LOT_SIZE') {
                $amountMin = $this->safe_number($filter, 'minQty');
                $amountPrecision = $this->safe_number($filter, 'stepSize');
            }
            if ($filterType === 'PRICE_FILTER') {
                $priceMin = $this->safe_number($filter, 'minPrice');
                $priceMax = $this->safe_number($filter, 'maxPrice');
                $pricePrecision = $this->safe_number($filter, 'tickSize');
            }
        }
        if (($amountMin !== null) && ($priceMin !== null)) {
            $costMin = $amountMin * $priceMin;
        }
        $precision = array(
            'price' => $pricePrecision,
            'amount' => $amountPrecision,
            'base' => $this->safe_number($market, 'baseAssetPrecision'),
            'quote' => $this->safe_number_2($market, 'quotePrecision', 'quoteAssetPrecision'),
        );
        $limits = array(
            'amount' => array(
                'min' => $amountMin,
                'max' => null,
            ),
            'price' => array(
                'min' => $priceMin,
                'max' => $priceMax,
            ),
            'cost' => array(
                'min' => $costMin,
                'max' => null,
            ),
        );
        return array(
            'id' => $id,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'active' => $active,
            'type' => $type,
            'spot' => $spot,
            'future' => $future,
            'option' => $option,
            'margin' => $margin,
            'inverse' => $inverse,
            'precision' => $precision,
            'limits' => $limits,
            'info' => $market,
        );
    }

