    public function fetch_balance($params = array ()) {
        $this->load_markets();
        $options = $this->safe_value($this->options, 'fetchBalance', array());
        $defaultType = $this->safe_string($options, 'type', 'spot');
        $type = $this->safe_string($params, 'type', $defaultType);
        $query = $this->omit($params, 'type');
        $method = 'privateGetAccount';
        if ($type === 'future') {
            $method = 'contractGetAccount';
        } else if ($type === 'option') {
            $method = 'optionGetAccount';
        }
        $response = $this->$method ($query);
        //
        // spot
        //
        //     {
        //         'balances' => array(
        //             array(
        //                 'asset' => 'ALGO',
        //                 'free' => '0',
        //                 'locked' => '0'
        //             ),
        //             {
        //                 'asset' => 'BHT',
        //                 'free' => '0',
        //                 'locked' => '0'
        //             }
        //         )
        //     }
        //
        // contract
        //
        //     {
        //         "BUSDT":array(
        //             "total":"1000",
        //             "availableMargin":"1000",
        //             "positionMargin":"0",
        //             "orderMargin":"0",
        //             "tokenId":"BUSDT"
        //         ),
        //         "TBTC":{
        //             "total":"0.5",
        //             "availableMargin":"0.5",
        //             "positionMargin":"0",
        //             "orderMargin":"0",
        //             "tokenId":"TBTC"
        //         }
        //     }
        //
        // option
        //
        //     {
        //         "optionAsset":"",
        //         "$balances":array(
        //             array(
        //                 "tokenName":"USDT",
        //                 "free":"0.0",
        //                 "locked":"0.0",
        //                 "margin":"0.0"
        //             ),
        //             {
        //                 "tokenName":"BUSDT",
        //                 "free":"0.0",
        //                 "locked":"0.0",
        //                 "margin":"0.0"
        //             }
        //         )
        //     }
        //
        $balances = $this->safe_value($response, 'balances');
        $result = array(
            'info' => $response,
            'timestamp' => null,
            'datetime' => null,
        );
        if ($balances !== null) {
            for ($i = 0; $i < count($balances); $i++) {
                $balance = $balances[$i];
                $currencyId = $this->safe_string_2($balance, 'asset', 'tokenName');
                $code = $this->safe_currency_code($currencyId);
                $account = $this->account();
                $account['free'] = $this->safe_string($balance, 'free');
                $account['used'] = $this->safe_string($balance, 'locked');
                $result[$code] = $account;
            }
        } else {
            $currencyIds = is_array($response) ? array_keys($response) : array();
            for ($i = 0; $i < count($currencyIds); $i++) {
                $currencyId = $currencyIds[$i];
                $code = $this->safe_currency_code($currencyId);
                $balance = $response[$currencyId];
                $account = $this->account();
                $account['free'] = $this->safe_string($balance, 'availableMargin');
                $account['total'] = $this->safe_string($balance, 'total');
                $result[$code] = $account;
            }
        }
        return $this->parse_balance($result);
    }

