    public function parse_ledger_entry($item, $currency = null) {
        //
        //     {
        //         "$id" => "539870570957903104",
        //         "accountId" => "122216245228131",
        //         "tokenId" => "BTC",
        //         "tokenName" => "BTC",
        //         "flowTypeValue" => 51,
        //         "flowType" => "USER_ACCOUNT_TRANSFER",
        //         "flowName" => "Transfer",
        //         "change" => "-12.5",
        //         "total" => "379.624059937852365", // $after change
        //         "created" => "1579093587214"
        //     }
        //
        //     {
        //         "$id" => "536072393645448960",
        //         "accountId" => "122216245228131",
        //         "tokenId" => "USDT",
        //         "tokenName" => "USDT",
        //         "flowTypeValue" => 7,
        //         "flowType" => "AIRDROP",
        //         "flowName" => "Airdrop",
        //         "change" => "-2000",
        //         "total" => "918662.0917630848",
        //         "created" => "1578640809195"
        //     }
        //
        $currencyId = $this->safe_string($item, 'tokenId');
        $code = $this->safe_currency_code($currencyId, $currency);
        $amount = $this->safe_number($item, 'change');
        $after = $this->safe_number($item, 'total');
        $direction = ($amount < 0) ? 'out' : 'in';
        $before = null;
        if ($after !== null && $amount !== null) {
            $difference = ($direction === 'out') ? $amount : -$amount;
            $before = $this->sum($after, $difference);
        }
        $timestamp = $this->safe_integer($item, 'created');
        $type = $this->parse_ledger_entry_type($this->safe_string($item, 'flowType'));
        $id = $this->safe_string($item, 'id');
        $account = $this->safe_string($item, 'accountId');
        return array(
            'id' => $id,
            'currency' => $code,
            'account' => $account,
            'referenceAccount' => null,
            'referenceId' => null,
            'status' => null,
            'amount' => $amount,
            'before' => $before,
            'after' => $after,
            'fee' => null,
            'direction' => $direction,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'type' => $type,
            'info' => $item,
        );
    }

