            $timestamp = $this->safe_integer($order, 'transactTime');
        }
        $marketId = $this->safe_string($order, 'symbol');
        $symbol = $this->safe_symbol($marketId, $market);
        $type = $this->safe_string_lower($order, 'type');
        $side = $this->safe_string_lower($order, 'side');
        $price = $this->safe_number($order, 'price');
        $average = $this->safe_number($order, 'avgPrice');
        $amount = null;
        $cost = $this->safe_number($order, 'cummulativeQuoteQty');
        $filled = null;
        $remaining = null;
        if ($type === null) {
            $type = $this->safe_string_lower($order, 'orderType');
            if (($market !== null) && $market['inverse']) {
                $cost = $this->safe_number($order, 'executedQty');
                $amount = null;
            }
            if ($cost === 0.0) {
                $filled = 0;
            }
        } else {
            $amount = $this->safe_number($order, 'origQty');
            if ($type === 'market') {
                $price = null;
                if ($side === 'buy') {
                    $amount = null;
                }
            }
            $filled = $this->safe_number($order, 'executedQty');
            if ($filled !== null) {
                if ($amount !== null) {
                    $remaining = $amount - $filled;
                }
            }
        }
        if ($average === 0.0) {
            $average = null;
        }
        $status = $this->parse_order_status($this->safe_string($order, 'status'));
        $timeInForce = $this->safe_string($order, 'timeInForce');
        $stopPrice = $this->safe_number($order, 'stopPrice');
        $result = array(
            'info' => $order,
            'id' => $id,
            'clientOrderId' => $clientOrderId,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => null,
            'symbol' => $symbol,
            'type' => $type,
            'timeInForce' => $timeInForce,
            'side' => $side,
            'price' => $price,
            'stopPrice' => $stopPrice,
            'average' => $average,
            'cost' => $cost,
            'amount' => $amount,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'trades' => null,
            'fee' => null,
            'fees' => null,
        );
        $fees = $this->safe_value($order, 'fees', array());
        $numFees = is_array($fees) ? count($fees) : 0;
        if ($numFees > 0) {
            $result['fees'] = array();
            for ($i = 0; $i < count($fees); $i++) {
                $feeCost = $this->safe_number($fees[$i], 'fee');
                if ($feeCost !== null) {
                    $feeCurrencyId = $this->safe_string($fees[$i], 'feeToken');
                    $feeCurrencyCode = $this->safe_currency_code($feeCurrencyId);
                    $result['fees'][] = array(
                        'cost' => $feeCost,
                        'currency' => $feeCurrencyCode,
                    );
                }
            }
        }
        return $result;
    }

    public function parse_order_status($status) {
        $statuses = array(
            'NEW' => 'open',
            'CANCELED' => 'canceled',
            'FILLED' => 'closed',
            'PARTIALLY_FILLED' => 'open',
            'PENDING_CANCEL' => 'canceled',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $url = $this->urls['api'][$api] . '/' . $this->version . '/' . $this->implode_params($path, $params);
        $query = $this->omit($params, $this->extract_params($path));
        $isPublicContract = ($api === 'contract') && (($path === 'insurance') || ($path === 'fundingRate'));
        if (($api === 'public') || ($api === 'quote') || $isPublicContract) {
            if ($params) {
                $url .= '?' . $this->urlencode($params);
            }
        } else {
            $timestamp = $this->milliseconds();
            $this->check_required_credentials();
            $request = array_merge(array(
                'timestamp' => $timestamp,
            ), $query);
            // 准备待签名数据
            $auth = $this->urlencode($request);
            $signature = $this->hmac($this->encode($auth), $this->encode($this->secret), 'sha256');
            $request['signature'] = $signature;
            $headers = array(
                'X-BH-APIKEY' => $this->apiKey,
            );
            if ($method === 'POST') {
                $body = $this->urlencode($request);
                $headers = array_merge(array(
                    'Content-Type' => 'application/x-www-form-urlencoded',
                ), $headers);
            } else {
                $url .= '?' . $this->urlencode($request);
            }
        }
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function handle_errors($httpCode, $reason, $url, $method, $headers, $body, $response, $requestHeaders, $requestBody) {
        if ($response === null) {
            return; // fallback to default error handler
        }
        if (is_array($response) && array_key_exists('code', $response)) {
            $code = $this->safe_string($response, 'code');
            if ($code !== '0') {
                $feedback = $this->id . ' ' . $body;
                $this->throw_exactly_matched_exception($this->exceptions['exact'], $code, $feedback);
                throw new ExchangeError($feedback);
            }
        }
    }
}
