        // explained here => https://github.com/ccxt/ccxt/issues/5674
        $request = array(
            'clientOrderId' => $id,
        );
        $response = $this->privateDeleteOrderClientOrderId (array_merge($request, $params));
        return $this->parse_order($response);
    }

    public function parse_order_status ($status) {
        $statuses = array(
            'new' => 'open',
            'suspended' => 'open',
            'partiallyFilled' => 'open',
            'filled' => 'closed',
            'canceled' => 'canceled',
            'expired' => 'failed',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order ($order, $market = null) {
        //
        // createMarketOrder
        //
        //   { clientOrderId =>   "fe36aa5e190149bf9985fb673bbb2ea0",
        //         createdAt =>   "2018-10-25T16:41:44.780Z",
        //       cumQuantity =>   "1",
        //                $id =>   "66799540063",
        //          quantity =>   "1",
        //              $side =>   "sell",
        //            $status =>   "$filled",
        //            $symbol =>   "XRPUSDT",
        //       timeInForce =>   "FOK",
        //      tradesReport => array( {       $fee => "0.0004644",
        //                               $id =>  386394956,
        //                            $price => "0.4644",
        //                         quantity => "1",
        //                        timestamp => "2018-10-25T16:41:44.780Z" } ),
        //              $type =>   "$market",
        //         updatedAt =>   "2018-10-25T16:41:44.780Z"                   }
        //
        $created = $this->parse8601 ($this->safe_string($order, 'createdAt'));
        $updated = $this->parse8601 ($this->safe_string($order, 'updatedAt'));
        $marketId = $this->safe_string($order, 'symbol');
        $symbol = null;
        if ($marketId !== null) {
            if (is_array($this->markets_by_id) && array_key_exists($marketId, $this->markets_by_id)) {
                $market = $this->markets_by_id[$marketId];
                $symbol = $market['symbol'];
            } else {
                $symbol = $marketId;
            }
        }
        if ($symbol === null) {
            if ($market !== null) {
                $symbol = $market['id'];
            }
        }
        $amount = $this->safe_float($order, 'quantity');
        $filled = $this->safe_float($order, 'cumQuantity');
        $status = $this->parse_order_status($this->safe_string($order, 'status'));
        // we use clientOrderId as the $order $id with HitBTC intentionally
        // because most of their endpoints will require clientOrderId
        // explained here => https://github.com/ccxt/ccxt/issues/5674
        $id = $this->safe_string($order, 'clientOrderId');
        $price = $this->safe_float($order, 'price');
        if ($price === null) {
            if (is_array($this->orders) && array_key_exists($id, $this->orders)) {
                $price = $this->orders[$id]['price'];
            }
        }
        $remaining = null;
        $cost = null;
        if ($amount !== null) {
            if ($filled !== null) {
                $remaining = $amount - $filled;
                if ($price !== null) {
                    $cost = $filled * $price;
                }
            }
        }
        $type = $this->safe_string($order, 'type');
        $side = $this->safe_string($order, 'side');
        $trades = $this->safe_value($order, 'tradesReport');
        $fee = null;
        $average = null;
        if ($trades !== null) {
            $trades = $this->parse_trades($trades, $market);
            $feeCost = null;
            $numTrades = is_array($trades) ? count($trades) : 0;
            $tradesCost = 0;
            for ($i = 0; $i < $numTrades; $i++) {
                if ($feeCost === null) {
                    $feeCost = 0;
                }
                $tradesCost = $this->sum ($tradesCost, $trades[$i]['cost']);
                $tradeFee = $this->safe_value($trades[$i], 'fee', array());
                $tradeFeeCost = $this->safe_float($tradeFee, 'cost');
                if ($tradeFeeCost !== null) {
                    $feeCost = $this->sum ($feeCost, $tradeFeeCost);
                }
            }
            $cost = $tradesCost;
            if (($filled !== null) && ($filled > 0)) {
                $average = $cost / $filled;
                if ($type === 'market') {
                    if ($price === null) {
                        $price = $average;
                    }
                }
            }
            if ($feeCost !== null) {
                $fee = array(
                    'cost' => $feeCost,
                    'currency' => $market['quote'],
                );
            }
        }
        return array(
            'id' => $id,
            'timestamp' => $created,
            'datetime' => $this->iso8601 ($created),
            'lastTradeTimestamp' => $updated,
            'status' => $status,
            'symbol' => $symbol,
            'type' => $type,
            'side' => $side,
            'price' => $price,
            'average' => $average,
            'amount' => $amount,
            'cost' => $cost,
            'filled' => $filled,
            'remaining' => $remaining,
            'fee' => $fee,
            'trades' => $trades,
            'info' => $order,
        );
    }

    public function fetch_order ($id, $symbol = null, $params = array ()) {
        $this->load_markets();
        // we use clientOrderId as the order $id with HitBTC intentionally
        // because most of their endpoints will require clientOrderId
        // explained here => https://github.com/ccxt/ccxt/issues/5674
        $request = array(
            'clientOrderId' => $id,
        );
        $response = $this->privateGetHistoryOrder (array_merge($request, $params));
        $numOrders = is_array($response) ? count($response) : 0;
        if ($numOrders > 0) {
            return $this->parse_order($response[0]);
        }
        throw new OrderNotFound($this->id . ' order ' . $id . ' not found');
    }

