                // 'payout' => $currency['withdraw-enabled'],
                // 'transfer' => null,
                'name' => $name,
                'active' => $active,
                'fee' => null, // todo need to fetch from fee endpoint
                'precision' => $precision,
                'limits' => array (
                    'amount' => array (
                        'min' => pow(10, -$precision),
                        'max' => pow(10, $precision),
                    ),
                    'price' => array (
                        'min' => pow(10, -$precision),
                        'max' => pow(10, $precision),
                    ),
                    'cost' => array (
                        'min' => null,
                        'max' => null,
                    ),
                    'deposit' => array (
                        'min' => $this->safe_float($currency, 'deposit-min-amount'),
                        'max' => pow(10, $precision),
                    ),
                    'withdraw' => array (
                        'min' => $this->safe_float($currency, 'withdraw-min-amount'),
                        'max' => pow(10, $precision),
                    ),
                ),
                'info' => $currency,
            );
        }
        return $result;
    }

    public function fetch_balance ($params = array ()) {
        $this->load_markets();
        $this->loadAccounts ();
        $method = $this->options['fetchBalanceMethod'];
        $request = array (
            'id' => $this->accounts[0]['id'],
        );
        $response = $this->$method (array_merge ($request, $params));
        $balances = $this->safe_value($response['data'], 'list', array());
        $result = array( 'info' => $response );
        for ($i = 0; $i < count ($balances); $i++) {
            $balance = $balances[$i];
            $currencyId = $this->safe_string($balance, 'currency');
            $code = $currencyId;
            if (is_array($this->currencies_by_id) && array_key_exists($currencyId, $this->currencies_by_id)) {
                $code = $this->currencies_by_id[$currencyId]['code'];
            } else {
                $code = $this->common_currency_code(strtoupper($currencyId));
            }
            $account = null;
            if (is_array($result) && array_key_exists($code, $result)) {
                $account = $result[$code];
            } else {
                $account = $this->account ();
            }
            if ($balance['type'] === 'trade') {
                $account['free'] = $this->safe_float($balance, 'balance');
            }
            if ($balance['type'] === 'frozen') {
                $account['used'] = $this->safe_float($balance, 'balance');
            }
            $result[$code] = $account;
        }
        return $this->parse_balance($result);
    }

