    public function fetch_open_orders_v2 ($symbol = null, $since = null, $limit = null, $params = array ()) {
        $this->load_markets();
        if ($symbol === null) {
            throw new ArgumentsRequired($this->id . ' fetchOpenOrders requires a $symbol argument');
        }
        $market = $this->market ($symbol);
        $accountId = $this->safe_string($params, 'account-id');
        if ($accountId === null) {
            // pick the first $account
            $this->loadAccounts ();
            for ($i = 0; $i < count ($this->accounts); $i++) {
                $account = $this->accounts[$i];
                if ($account['type'] === 'spot') {
                    $accountId = $this->safe_string($account, 'id');
                    if ($accountId !== null) {
                        break;
                    }
                }
            }
        }
        $request = array (
            'symbol' => $market['id'],
            'account-id' => $accountId,
        );
        if ($limit !== null) {
            $request['size'] = $limit;
        }
        $omitted = $this->omit ($params, 'account-id');
        $response = $this->privateGetOrderOpenOrders (array_merge ($request, $omitted));
        //
        //     {
        //         "status":"ok",
        //         "$data":array (
        //             {
        //                 "$symbol":"ethusdt",
        //                 "source":"api",
        //                 "amount":"0.010000000000000000",
        //                 "$account-id":1528640,
        //                 "created-at":1561597491963,
        //                 "price":"400.000000000000000000",
        //                 "filled-amount":"0.0",
        //                 "filled-cash-amount":"0.0",
        //                 "filled-fees":"0.0",
        //                 "id":38477101630,
        //                 "state":"submitted",
        //                 "type":"sell-$limit"
        //             }
        //         )
        //     }
        //
        $data = $this->safe_value($response, 'data', array());
        return $this->parse_orders($data, $market, $since, $limit);
    }

    public function parse_order_status ($status) {
        $statuses = array (
            'partial-filled' => 'open',
            'partial-canceled' => 'canceled',
