            'submitted' => 'open',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order ($order, $market = null) {
        //
        //     {                  $id =>  13997833014,
        //                    $symbol => "ethbtc",
        //              'account-id' =>  3398321,
        //                    $amount => "0.045000000000000000",
        //                     $price => "0.034014000000000000",
        //              'created-at' =>  1545836976871,
        //                      $type => "sell-limit",
        //            'field-amount' => "0.045000000000000000", // they have fixed it for $filled-$amount
        //       'field-cash-amount' => "0.001530630000000000", // they have fixed it for $filled-cash-$amount
        //              'field-fees' => "0.000003061260000000", // they have fixed it for $filled-fees
        //             'finished-at' =>  1545837948214,
        //                    source => "spot-api",
        //                     state => "$filled",
        //             'canceled-at' =>  0                      }
        //
        //     {                  $id =>  20395337822,
        //                    $symbol => "ethbtc",
        //              'account-id' =>  5685075,
        //                    $amount => "0.001000000000000000",
        //                     $price => "0.0",
        //              'created-at' =>  1545831584023,
        //                      $type => "buy-$market",
        //            'field-amount' => "0.029100000000000000", // they have fixed it for $filled-$amount
        //       'field-cash-amount' => "0.000999788700000000", // they have fixed it for $filled-cash-$amount
        //              'field-fees' => "0.000058200000000000", // they have fixed it for $filled-fees
        //             'finished-at' =>  1545831584181,
        //                    source => "spot-api",
        //                     state => "$filled",
        //             'canceled-at' =>  0                      }
        //
        $id = $this->safe_string($order, 'id');
        $side = null;
        $type = null;
        $status = null;
        if (is_array($order) && array_key_exists('type', $order)) {
            $orderType = explode('-', $order['type']);
            $side = $orderType[0];
            $type = $orderType[1];
            $status = $this->parse_order_status($this->safe_string($order, 'state'));
        }
        $symbol = null;
        if ($market === null) {
            if (is_array($order) && array_key_exists('symbol', $order)) {
                if (is_array($this->markets_by_id) && array_key_exists($order['symbol'], $this->markets_by_id)) {
                    $marketId = $order['symbol'];
                    $market = $this->markets_by_id[$marketId];
                }
            }
        }
        if ($market !== null) {
            $symbol = $market['symbol'];
        }
        $timestamp = $this->safe_integer($order, 'created-at');
        $amount = $this->safe_float($order, 'amount');
        $filled = $this->safe_float_2($order, 'filled-amount', 'field-amount'); // typo in their API, $filled $amount
        if (($type === 'market') && ($side === 'buy')) {
            $amount = ($status === 'closed') ? $filled : null;
        }
        $price = $this->safe_float($order, 'price');
        if ($price === 0.0) {
            $price = null;
        }
        $cost = $this->safe_float_2($order, 'filled-cash-amount', 'field-cash-amount'); // same typo
        $remaining = null;
        $average = null;
        if ($filled !== null) {
            if ($amount !== null) {
                $remaining = $amount - $filled;
            }
            // if $cost is defined and $filled is not zero
            if (($cost !== null) && ($filled > 0)) {
                $average = $cost / $filled;
            }
        }
        $feeCost = $this->safe_float_2($order, 'filled-fees', 'field-fees'); // typo in their API, $filled fees
        $fee = null;
        if ($feeCost !== null) {
            $feeCurrency = null;
            if ($market !== null) {
                $feeCurrency = ($side === 'sell') ? $market['quote'] : $market['base'];
            }
            $fee = array (
                'cost' => $feeCost,
                'currency' => $feeCurrency,
            );
        }
        return array (
            'info' => $order,
            'id' => $id,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601 ($timestamp),
            'lastTradeTimestamp' => null,
            'symbol' => $symbol,
            'type' => $type,
            'side' => $side,
            'price' => $price,
            'average' => $average,
            'cost' => $cost,
            'amount' => $amount,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'fee' => $fee,
        );
    }

    public function create_order ($symbol, $type, $side, $amount, $price = null, $params = array ()) {
        $this->load_markets();
        $this->loadAccounts ();
        $market = $this->market ($symbol);
        $request = array (
            'account-id' => $this->accounts[0]['id'],
            'amount' => $this->amount_to_precision($symbol, $amount),
            'symbol' => $market['id'],
            'type' => $side . '-' . $type,
        );
        if ($this->options['createMarketBuyOrderRequiresPrice']) {
            if (($type === 'market') && ($side === 'buy')) {
                if ($price === null) {
                    throw new InvalidOrder($this->id . " $market buy order requires $price argument to calculate cost (total $amount of quote currency to spend for buying, $amount * $price). To switch off this warning exception and specify cost in the $amount argument, set .options['createMarketBuyOrderRequiresPrice'] = false. Make sure you know what you're doing.");
                } else {
                    // despite that cost = $amount * $price is in quote currency and should have quote precision
                    // the exchange API requires the cost supplied in 'amount' to be of base precision
                    // more about it here => https://github.com/ccxt/ccxt/pull/4395
                    // we use priceToPrecision instead of amountToPrecision here
                    // because in this case the $amount is in the quote currency
                    $request['amount'] = $this->price_to_precision($symbol, floatval ($amount) * floatval ($price));
                }
            }
        }
        if ($type === 'limit' || $type === 'ioc' || $type === 'limit-maker') {
            $request['price'] = $this->price_to_precision($symbol, $price);
        }
        $method = $this->options['createOrderMethod'];
        $response = $this->$method (array_merge ($request, $params));
        $timestamp = $this->milliseconds ();
        $id = $this->safe_string($response, 'data');
        return array (
            'info' => $response,
            'id' => $id,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601 ($timestamp),
            'lastTradeTimestamp' => null,
            'status' => null,
            'symbol' => $symbol,
            'type' => $type,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'filled' => null,
            'remaining' => null,
            'cost' => null,
            'trades' => null,
            'fee' => null,
        );
    }

