    public function parse_currency(array $currency): array {
        //
        //    {
        //        "code" => "BTC",
        //        "name" => "Bitcoin",
        //        "payload" => array(
        //            "chart" => "https://kuna-pro.kuna.io/bitcoin-chart",
        //            "icons" => array(
        //                "svg" => "https://kuna-pro.kuna.io/icon-btc-svg",
