    public function parse_order_status(?string $status) {
        $statuses = array(
            'Canceled' => 'canceled',
            'Closed' => 'filled',
            'Pending' => 'open',
            'Open' => 'open',
            'done' => 'closed',
            'wait' => 'open',
            'cancel' => 'canceled',
        );
        return $this->safe_string($statuses, $status, $status);
    }

