    public function parse_transaction_status(?string $status) {
        $statuses = array(
            'Created' => 'pending',
            'Canceled' => 'canceled',
            'PartiallyProcessed' => 'pending',
            'Processing' => 'pending',
            'Processed' => 'ok',
            'WaitForConfirmation' => 'pending',
            'Pending' => 'pending',
            'AmlChecking' => 'pending',
        );
        return $this->safe_string($statuses, $status, $status);
    }

