    public function parse_ticker($ticker, ?array $market = null): array {
        //
        // spot => fetchTicker, fetchTickers
        //
        //     {
        //         "symbol" => "btc_usdt",
        //         "ticker" => array(
        //             "high" => "29695.57",
        //             "vol" => "6890.2789",
        //             "low" => "29110",
        //             "change" => "0.58",
        //             "turnover" => "202769821.06",
        //             "latest" => "29405.98"
        //         ),
        //         "timestamp" => :1692064274908
        //     }
        //
        // swap => fetchTickers
        //
        //     {
        //         "prePositionFeeRate" => "0.000053",
        //         "volume" => "2435.459",
        //         "symbol" => "BTCUSDT",
        //         "highestPrice" => "29446.5",
        //         "lowestPrice" => "29362.9",
        //         "openPrice" => "29419.5",
        //         "markedPrice" => "29385.1",
        //         "turnover" => "36345526.2438402",
        //         "lastPrice" => "29387.0"
        //     }
        //
        $timestamp = $this->safe_integer($ticker, 'timestamp');
        $marketId = $this->safe_string($ticker, 'symbol');
        $symbol = $this->safe_symbol($marketId, $market);
        $tickerData = $this->safe_value($ticker, 'ticker', array());
        $market = $this->safe_market($marketId, $market);
        $data = ($market['contract']) ? $ticker : $tickerData;
        return $this->safe_ticker(array(
            'symbol' => $symbol,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'high' => $this->safe_string_2($data, 'high', 'highestPrice'),
            'low' => $this->safe_string_2($data, 'low', 'lowestPrice'),
            'bid' => null,
            'bidVolume' => null,
            'ask' => null,
            'askVolume' => null,
            'vwap' => null,
            'open' => $this->safe_string($data, 'openPrice'),
            'close' => null,
            'last' => $this->safe_string_2($data, 'latest', 'lastPrice'),
            'previousClose' => null,
            'change' => null,
            'percentage' => $this->safe_string($data, 'change'),
            'average' => null,
            'baseVolume' => $this->safe_string_2($data, 'vol', 'volume'),
            'quoteVolume' => $this->safe_string($data, 'turnover'),
            'info' => $ticker,
        ), $market);
    }

    public function fetch_ticker(string $symbol, $params = array ()): array {
        /**
         * fetches a price ticker, a statistical calculation with the information calculated over the past 24 hours for a specific $market
         * @see https://www.lbank.com/en-US/docs/index.html#query-current-$market-$data-new
         * @param {string} $symbol unified $symbol of the $market to fetch the ticker for
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a ~@link https://docs.ccxt.com/#/?id=ticker-structure ticker structure~
         */
        $this->load_markets();
        $market = $this->market($symbol);
        if ($market['swap']) {
            $responseForSwap = $this->fetch_tickers([ $market['symbol'] ], $params);
            return $this->safe_value($responseForSwap, $market['symbol']);
        }
        $request = array(
            'symbol' => $market['id'],
        );
        $response = $this->spotPublicGetTicker24hr (array_merge($request, $params));
        //
        //     {
        //         "result" => "true",
        //         "data" => array(
        //             {
        //                 "symbol" => "btc_usdt",
        //                 "ticker" => array(
        //                     "high" => "29695.57",
        //                     "vol" => "6890.2789",
        //                     "low" => "29110",
        //                     "change" => "0.58",
        //                     "turnover" => "202769821.06",
        //                     "latest" => "29405.98"
        //                 ),
        //                 "timestamp" => :1692064274908
        //             }
        //         ),
        //         "error_code" => 0,
        //         "ts" => :1692064276872
        //     }
        //
        $data = $this->safe_value($response, 'data', array());
        $first = $this->safe_dict($data, 0, array());
        return $this->parse_ticker($first, $market);
    }

    public function fetch_tickers(?array $symbols = null, $params = array ()): array {
        /**
         * fetches price tickers for multiple markets, statistical information calculated over the past 24 hours for each $market
         * @see https://www.lbank.com/en-US/docs/index.html#query-current-$market-$data-new
         * @see https://www.lbank.com/en-US/docs/contract.html#query-contract-$market-list
         * @param {string[]|null} $symbols unified $symbols of the markets to fetch the ticker for, all $market tickers are returned if not assigned
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=ticker-structure ticker structures~
         */
        $this->load_markets();
        $market = null;
        if ($symbols !== null) {
            $symbols = $this->market_symbols($symbols);
            $symbolsLength = count($symbols);
            if ($symbolsLength > 0) {
                $market = $this->market($symbols[0]);
            }
        }
        $request = array();
        $type = null;
        list($type, $params) = $this->handle_market_type_and_params('fetchTickers', $market, $params);
        $response = null;
        if ($type === 'swap') {
            $request['productGroup'] = 'SwapU';
            $response = $this->contractPublicGetCfdOpenApiV1PubMarketData (array_merge($request, $params));
