        $this->load_markets();
        $code = $this->safe_string_2($params, 'coin', 'assetCode');
        $params = $this->omit($params, array( 'coin', 'assetCode' ));
        $request = array();
        if ($code !== null) {
            $currency = $this->currency($code);
            $request['assetCode'] = $currency['id'];
