    public function fetch_markets($params = array ()) {
        /**
         * retrieves $data on all markets for lbank2
         * @param {array} $params extra parameters specific to the exchange api endpoint
         * @return {[array]} an array of objects representing $market $data
         */
        // needs to return a list of unified $market structures
        $response = $this->publicGetAccuracy ();
        $data = $this->safe_value($response, 'data');
        //      array(
        //          {
        //              $symbol => 'snx3s_usdt',
        //              quantityAccuracy => '2',
        //              minTranQua => '0.01',
        //              priceAccuracy => '6'
        //          }
        //     )
        $result = array();
        for ($i = 0; $i < count($data); $i++) {
            $market = $data[$i];
            $marketId = $this->safe_string($market, 'symbol');
            $parts = explode('_', $marketId);
            $baseId = $parts[0];
            $quoteId = $parts[1];
            $base = strtoupper($baseId);
            $quote = strtoupper($quoteId);
            $symbol = $base . '/' . $quote;
            $productTypes = array(
                '3l' => true,
                '5l' => true,
                '3s' => true,
                '5s' => true,
            );
            $ending = mb_substr($baseId, -2);
            $isLeveragedProduct = $this->safe_value($productTypes, $ending, false);
            if ($isLeveragedProduct) {
                $symbol .= ':' . $quote;
            }
            $linear = null;
            if ($isLeveragedProduct === true) {
                $linear = true;
            }
            $result[] = array(
                'id' => $marketId,
                'symbol' => $symbol,
                'base' => $base,
                'quote' => $quote,
                'settle' => null,
                'baseId' => $baseId,
                'quoteId' => $quoteId,
                'settleId' => null,
                'type' => 'spot',
                'spot' => true,
                'margin' => false,
                'swap' => $isLeveragedProduct,
                'future' => false,
                'option' => false,
                'active' => true,
                'contract' => $isLeveragedProduct,
                'linear' => $linear, // all leveraged ETF products are in USDT
                'inverse' => null,
                'contractSize' => null,
                'expiry' => null,
                'expiryDatetime' => null,
                'strike' => null,
                'optionType' => null,
                'precision' => array(
                    'amount' => $this->parse_number($this->parse_precision($this->safe_string($market, 'quantityAccuracy'))),
                    'price' => $this->parse_number($this->parse_precision($this->safe_string($market, 'priceAccuracy'))),
