    public function fetch_trading_fees($params = array ()) {
        $this->load_markets();
        $response = $this->privateGetFee ($params);
        //
        // {
        //     "maker" => "0.15",
        //     "taker" => "0.25",
        //     "volume" => "11.70"
        // }
        //
        $result = array();
        $maker = $this->safe_number($response, 'maker');
        if ($maker !== null) {
            // convert to ratio
            $maker = $maker / 100;
        }
        $taker = $this->safe_number($response, 'taker');
        if ($taker !== null) {
            // convert to ratio
            $taker = $taker / 100;
        }
        for ($i = 0; $i < count($this->symbols); $i++) {
            $symbol = $this->symbols[$i];
            $result[$symbol] = array(
                'maker' => $maker,
                'taker' => $taker,
                'info' => $response,
                'symbol' => $symbol,
            );
        }
        return $result;
    }

