    public function fetch_markets($params = array ()) {
        $response = $this->publicGetCommonSymbols ($params);
        //
        //     {
        //         "code":"A10000",
        //         "$data":array(
        //             array(
        //                 "amountPrecision":8,
        //                 "baseCurrency":"BTC",
        //                 "minOrderAmount":"0.001",
