    public function parse_trade($trade, $market = null) {
        //
        // public fetchTrades
        //
        //     {
        //         "$amount":"0.0632",
        //         "$price":"0.037288",
        //         "$side":"BUY",
        //         "$timestamp":1599279694576
        //     }
        //
        // private fetchOrderTrades
        //
        //     {
        //         "$id" => "608717046691139584",
        //         "$orderId" => "608716957545402368",
        //         "$symbol" => "BTC_BRL",
        //         "$side" => "BUY",
        //         "$amount" => "0.0988",
        //         "$price" => "45514.76",
        //         "$fee" => "0.0000988 BTC",
        //         "role" => "MAKER",
        //         "$timestamp" => 1565171053345
        //     }
        //
        // private fetchMyTrades
        //
        //     {
        //         "$id" => "608717046691139584",
        //         "$orderId" => "608716957545402368",
        //         "$symbol" => "BTC_BRL",
        //         "$side" => "BUY",
        //         "$amount" => "0.0988",
        //         "$price" => "45514.76",
        //         "$fee" => "0.0000988 BTC",
        //         "feeAmount" => "0.0000988",
        //         "feeCurrency" => "BTC",
        //         "role" => "MAKER",
        //         "$timestamp" => 1565171053345
        //     }
        //
        $id = $this->safe_string($trade, 'id');
        $orderId = $this->safe_string($trade, 'orderId');
        $timestamp = $this->safe_integer($trade, 'timestamp');
        $side = $this->safe_string_lower($trade, 'side');
        $priceString = $this->safe_string($trade, 'price');
        $amountString = $this->safe_string($trade, 'amount');
        $price = $this->parse_number($priceString);
        $amount = $this->parse_number($amountString);
        $cost = $this->safe_number($trade, 'volume');
        if ($cost === null) {
            $cost = $this->parse_number(Precise::string_mul($priceString, $amountString));
        }
        $marketId = $this->safe_string($trade, 'symbol');
        $symbol = $this->safe_symbol($marketId, $market, '_');
        $takerOrMaker = $this->safe_string_lower($trade, 'role');
        $feeString = $this->safe_string($trade, 'fee');
        $fee = null;
        if ($feeString !== null) {
            $parts = explode(' ', $feeString);
            $feeCurrencyId = $this->safe_string($parts, 1);
            $feeCurrencyCode = $this->safe_currency_code($feeCurrencyId);
            $fee = array(
                'cost' => $this->safe_number($parts, 0),
                'currency' => $feeCurrencyCode,
            );
        }
        return array(
            'id' => $id,
            'order' => $orderId,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'type' => null,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'takerOrMaker' => $takerOrMaker,
            'fee' => $fee,
            'info' => $trade,
        );
    }

