    public function parse_order_status(?string $status) {
        $statuses = array(
            'Created' => 'open',
            'Untriggered' => 'open',
            'Deactivated' => 'closed',
            'Triggered' => 'open',
            'Rejected' => 'rejected',
            'New' => 'open',
            'PartiallyFilled' => 'open',
            'Filled' => 'closed',
            'Canceled' => 'canceled',
            'Suspended' => 'canceled',
            '1' => 'open',
            '2' => 'canceled',
            '3' => 'closed',
            '4' => 'canceled',
            '5' => 'open',
            '6' => 'open',
            '7' => 'closed',
            '8' => 'canceled',
        );
        return $this->safe_string($statuses, $status, $status);
    }

