        //             "feeRv" => "1",
        //             "nickName" => null,
        //             "phone" => null,
        //             "rejectReason" => "",
        //             "submitedAt" => "1670000000000",
        //             "submittedAt" => "1670000000000",
        //             "txHash" => null,
        //             "userId" => "10000001",
        //             "status" => "Success"
        //         }
        //     }
