    public function parse_ticker($ticker, ?array $market = null): array {
        // {
        //   "market" => "BTC_IRT",
        //   "marketId" => 8,
        //   "marketVolume" => "27140908320.17800246693400000000000000",
        //   "currentPrice" => "12909088630.0000000000000",
