        $market = $this->market($symbol);
        return $this->fetch_order_book($market['id'], null, array( 'level' => 3 ));
    }

    public function parse_trade($trade, $market = null) {
        //
        // fetchTrades (public)
        //
        //     {
        //         "sequence" => 11827985,
        //         "side" => "buy",
        //         "size" => 101,
        //         "price" => "16864.0000000000",
        //         "takerOrderId" => "639c986f0ac2470007be75ee",
