        //                    "isActive" => true,
        //                    "createdAt" => 1678936920000,
        //                    "orderTime" => 1678936920480905922,
        //                    "price" => "0.3",
        //                    "iceberg" => false,
        //                    "stopTriggered" => false,
        //                    "id" => "64128b582cc0710007a3c840",
        //                    "value" => "3",
        //                    "timeInForce" => "GTC",
        //                    "updatedAt" => 1678936920000,
        //                    "side" => "buy",
        //                    "stopPriceType" => "",
        //                    "dealValue" => "0",
        //                    "dealSize" => 0,
        //                    "settleCurrency" => "USDT",
        //                    "stp" => "",
        //                    "filledValue" => "0",
        //                    "postOnly" => false,
        //                    "size" => 1,
        //                    "stop" => "",
        //                    "filledSize" => 0,
        //                    "reduceOnly" => false,
        //                    "marginType" => 1,
        //                    "cancelExist" => false,
        //                    "clientOid" => "ba669f39-dfcc-4664-9801-a42d06e59c2e",
        //                    "status" => "open"
        //                }
        //            )
        //        }
        //    }
        //
        $responseData = $this->safe_value($response, 'data', array());
        $orders = $this->safe_value($responseData, 'items', array());
        $ordersLength = count($orders);
        $result = array();
        for ($i = 0; $i < $ordersLength; $i++) {
            $order = $orders[$i];
            $orderStatus = $this->safe_string($order, 'status');
            if ($status === $orderStatus) {
                $result[] = $orders[$i];
            }
        }
        return $this->parse_orders($result, $market, $since, $limit);
    }

