    public function handle_order(Client $client, $message) {
        //
        //     {
        //         $data => array(
        //             E => '1754939110175843',
        //             O => 'USER',
        //             Q => '4.30',
        //             S => 'Bid',
        //             T => '1754939110174703',
        //             V => 'RejectTaker',
        //             X => 'New',
        //             Z => '0',
        //             e => 'orderAccepted',
        //             f => 'GTC',
        //             i => '5406825793',
        //             o => 'MARKET',
        //             q => '0.0010',
        //             r => false,
        //             s => 'ETH_USDC',
        //             t => null,
        //             z => '0'
        //         ),
        //         stream => 'account.orderUpdate.ETH_USDC'
        //     }
        //
        $messageHash = 'orders';
        $data = $this->safe_dict($message, 'data', array());
        $marketId = $this->safe_string($data, 's');
