        //        execType => 'T',
        //        accFillSz => '0.0959',
        //        avgPx => '52.11',
        //        $status => 'partial-fill',
        //        cTime => 1656502996972,
        //        uTime => 1656502997119,
        //        $orderFee => [Array]
        //    }
        //
        // contract $order
        //    {
        //        accFillSz => '0',
        //        cTime => 1656510642518,
        //        clOrdId => '926038241960431617',
        //        force => 'normal',
        //        instId => 'LTCUSDT_UMCBL',
        //        lever => '20',
        //        notionalUsd => '7.5',
