        //        )
        //        posSide => 'long',
        //        px => '25',
        //        $side => 'buy',
        //        $status => 'new',
        //        sz => '0.3',
        //        tdMode => 'cross',
        //        tgtCcy => 'USDT',
        //        uTime => 1656510642518
        //    }
        // algo $order
        //    {
        //        "actualPx":"50.000000000",
        //        "actualSz":"0.000000000",
        //        "cOid":"1041588152132243456",
        //        "cTime":"1684059887917",
        //        "eps":"api",
        //        "hM":"double_hold",
        //        "id":"1041588152132243457",
        //        "instId":"LTCUSDT_UMCBL",
        //        "key":"1041588152132243457",
        //        "ordPx":"55.000000000",
        //        "ordType":"limit",
        //        "planType":"pl",
        //        "posSide":"long",
        //        "side":"buy",
        //        "state":"not_trigger",
        //        "sz":"0.100000000",
        //        "tS":"open_long",
        //        "tgtCcy":"USDT",
        //        "triggerPx":"55.000000000",
        //        "triggerPxType":"mark",
        //        "triggerTime":"1684059887917",
