        //           "feeDetail" => array(
        //              {
        //                 "feeCoin" => "LTC",
        //                 "deduction" => "no",
        //                 "totalDeductionFee" => "0",
        //                 "totalFee" => "0.000074"
        //              }
        //           ),
        //           "cTime" => "1714471204194",
        //           "uTime" => "1714471204194"
        //        }
        //
        // uta private
        //
        //     {
        //         "symbol" => "BTCUSDT",
        //         "orderType" => "market",
        //         "updatedTime" => "1736378720623",
        //         "side" => "buy",
        //         "orderId" => "1288888888888888888",
        //         "execPnl" => "0",
        //         "feeDetail" => array(
        //             {
        //                 "feeCoin" => "USDT",
        //                 "fee" => "0.569958"
        //             }
        //         ),
        //         "execTime" => "1736378720623",
        //         "tradeScope" => "taker",
        //         "tradeSide" => "open",
        //         "execId" => "1288888888888888888",
