        //         priceAvg => '0.00000', // in case of 'filled', this would be set
        //         status => 'live', // live, filled, partially_filled
        //         cTime => '1715099824215',
        //         uTime => '1715099824215',
        //         feeDetail => array(),
        //         enterPointSource => 'API'
        //                   #### trigger $order has these additional fields => ####
        //         "triggerPrice" => "35100",
        //         "price" => "35100", // this is same $price
        //         "executePrice" => "35123", // this is limit $price
        //         "triggerType" => "fill_price",
        //         "planType" => "amount",
        //                   #### in case $order had a partial fill => ####
        //         fillPrice => '35123',
        //         tradeId => '1171775539946528779',
        //         baseVolume => '7', // field present in $market $order
        //         fillTime => '1715098979937',
        //         fillFee => '-0.0069987',
        //         fillFeeCoin => 'BTC',
        //         tradeScope => 'T',
        //    }
        //
        // contract
        //
        //     {
        //         $accBaseVolume => '0', // total amount filled during lifetime for $order
        //         cTime => '1715065875539',
        //         clientOid => '1171636690041344003',
        //         enterPointSource => 'API',
        //         feeDetail => array( {
        //             "feeCoin" => "USDT",
        //             "fee" => "-0.162003"
        //         } ),
        //         force => 'gtc',
        //         instId => 'SEOSSUSDT',
        //         leverage => '10',
        //         marginCoin => 'USDT',
        //         marginMode => 'crossed',
        //         notionalUsd => '10.4468',
        //         orderId => '1171636690028761089',
        //         orderType => 'market',
        //         posMode => 'hedge_mode', // one_way_mode, hedge_mode
        //         posSide => 'short', // short, long, net
        //         $price => '0', // zero for $market $order
        //         reduceOnly => 'no',
        //         $side => 'sell',
        //         size => '13', // this is contracts amount
        //         status => 'live', // live, filled, cancelled
        //         tradeSide => 'open',
        //         uTime => '1715065875539'
        //                   #### when filled $order is incoming, these additional fields are present too => ###
        //         baseVolume => '9', // amount filled for the incoming update/trade
        //         $accBaseVolume => '13', // i.e. 9 has been filled from 13 amount (this value is same as 'size')
        //         fillFee => '-0.0062712',
        //         fillFeeCoin => 'SUSDT',
        //         fillNotionalUsd => '10.452',
        //         fillPrice => '0.804',
        //         fillTime => '1715065875605',
        //         pnl => '0',
        //         priceAvg => '0.804',
        //         tradeId => '1171636690314407937',
        //         tradeScope => 'T',
        //                   #### trigger $order has these additional fields:
        //         "triggerPrice" => "0.800000000",
        //         "price" => "0.800000000",  // <-- this is same $price, actual limit-$price is not present in initial response
        //         "triggerType" => "mark_price",
        //         "triggerTime" => "1715082796679",
        //         "planType" => "pl",
        //         "actualSize" => "0.000000000",
        //         "stopSurplusTriggerType" => "fill_price",
        //         "stopLossTriggerType" => "fill_price",
        //     }
        //
        // isolated and cross margin
        //
        //     {
        //         enterPointSource => "web",
        //         feeDetail => array(
        //           array(
        //             feeCoin => "AAVE",
        //             deduction => "no",
        //             totalDeductionFee => "0",
        //             totalFee => "-0.00010740",
        //           ),
        //         ),
        //         force => "gtc",
        //         orderType => "limit",
        //         $price => "93.170000000",
        //         fillPrice => "93.170000000",
        //         baseSize => "0.110600000", // total amount of $order
        //         quoteSize => "10.304602000", // total $cost of $order (independently if $order is filled or pending)
        //         baseVolume => "0.107400000", // filled amount of $order (during order's lifecycle, and not for this specific incoming update)
        //         fillTotalAmount => "10.006458000", // filled $cost of $order (during order's lifecycle, and not for this specific incoming update)
        //         $side => "buy",
        //         status => "partially_filled",
        //         cTime => "1717875017306",
        //         clientOid => "b57afe789a06454e9c560a2aab7f7201",
        //         loanType => "auto-loan",
        //         orderId => "1183419084588060673",
        //       }
        //
        // uta
        //
        //     {
        //         "category" => "usdt-futures",
        //         "symbol" => "BTCUSDT",
        //         "orderId" => "xxx",
        //         "clientOid" => "xxx",
        //         "price" => "",
        //         "qty" => "0.001",
        //         "amount" => "1000",
        //         "holdMode" => "hedge_mode",
        //         "holdSide" => "long",
        //         "tradeSide" => "open",
        //         "orderType" => "market",
        //         "timeInForce" => "gtc",
        //         "side" => "buy",
        //         "marginMode" => "crossed",
        //         "marginCoin" => "USDT",
        //         "reduceOnly" => "no",
        //         "cumExecQty" => "0.001",
        //         "cumExecValue" => "83.1315",
        //         "avgPrice" => "83131.5",
        //         "totalProfit" => "0",
        //         "orderStatus" => "filled",
        //         "cancelReason" => "",
        //         "leverage" => "20",
        //         "feeDetail" => array(
        //             {
        //                 "feeCoin" => "USDT",
        //                 "fee" => "0.0332526"
        //             }
        //         ),
        //         "createdTime" => "1742367838101",
        //         "updatedTime" => "1742367838115",
        //         "stpMode" => "none"
        //     }
        //
        $isSpot = !(is_array($order) && array_key_exists('posMode', $order));
        $isMargin = (is_array($order) && array_key_exists('loanType', $order));
        $category = $this->safe_string_lower($order, 'category');
        if ($category === 'spot') {
            $isSpot = true;
        }
        if ($category === 'margin') {
            $isMargin = true;
        }
        $marketId = $this->safe_string_2($order, 'instId', 'symbol');
        $market = $this->safe_market($marketId, $market);
        $timestamp = $this->safe_integer_2($order, 'cTime', 'createdTime');
        $symbol = $market['symbol'];
        $rawStatus = $this->safe_string_2($order, 'status', 'orderStatus');
        $orderFee = $this->safe_value($order, 'feeDetail', array());
        $fee = $this->safe_value($orderFee, 0);
        $feeAmount = $this->safe_string($fee, 'fee');
        $feeObject = null;
        if ($feeAmount !== null) {
            $feeCurrency = $this->safe_string($fee, 'feeCoin');
            $feeObject = array(
                'cost' => $this->parse_number(Precise::string_abs($feeAmount)),
                'currency' => $this->safe_currency_code($feeCurrency),
            );
        }
        $triggerPrice = $this->safe_number($order, 'triggerPrice');
        $isTriggerOrder = ($triggerPrice !== null);
        $price = null;
        if (!$isTriggerOrder) {
            $price = $this->safe_number($order, 'price');
        } elseif ($isSpot && $isTriggerOrder) {
            // for spot trigger $order, limit $price is this
            $price = $this->safe_number($order, 'executePrice');
        }
        $avgPrice = $this->omit_zero($this->safe_string_lower_n($order, array( 'priceAvg', 'fillPrice', 'avgPrice' )));
        $side = $this->safe_string($order, 'side');
        $type = $this->safe_string($order, 'orderType');
        $accBaseVolume = $this->omit_zero($this->safe_string_2($order, 'accBaseVolume', 'cumExecQty'));
        $newSizeValue = $this->omit_zero($this->safe_string_2($order, 'newSize', 'cumExecValue'));
        $isMarketOrder = ($type === 'market');
        $isBuy = ($side === 'buy');
        $totalAmount = null;
        $filledAmount = null;
        $cost = null;
        $remaining = null;
        $totalFilled = $this->safe_string_2($order, 'accBaseVolume', 'cumExecQty');
        if ($isSpot) {
            if ($isMargin) {
                $totalAmount = $this->safe_string_2($order, 'baseSize', 'qty');
                $totalFilled = $this->safe_string_2($order, 'baseVolume', 'cumExecQty');
                $cost = $this->safe_string_2($order, 'fillTotalAmount', 'cumExecValue');
            } else {
                $partialFillAmount = $this->safe_string($order, 'baseVolume');
                if ($partialFillAmount !== null) {
                    $filledAmount = $partialFillAmount;
                } else {
                    $filledAmount = $totalFilled;
                }
                if ($isMarketOrder) {
                    if ($isBuy) {
                        $totalAmount = $accBaseVolume;
                        $cost = $newSizeValue;
                    } else {
                        $totalAmount = $newSizeValue;
                        // we don't have $cost for $market-sell $order
                    }
                } else {
                    $totalAmount = $this->safe_string_2($order, 'newSize', 'qty');
                    // we don't have $cost for limit $order
                }
            }
        } else {
            // baseVolume should not be used for "amount" for contracts !
            $filledAmount = $this->safe_string_2($order, 'baseVolume', 'cumExecQty');
            $totalAmount = $this->safe_string_2($order, 'size', 'qty');
            $cost = $this->safe_string_2($order, 'fillNotionalUsd', 'cumExecValue');
        }
        $remaining = Precise::string_sub($totalAmount, $totalFilled);
        return $this->safe_order(array(
            'info' => $order,
            'symbol' => $symbol,
            'id' => $this->safe_string($order, 'orderId'),
            'clientOrderId' => $this->safe_string($order, 'clientOid'),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => $this->safe_integer_2($order, 'uTime', 'updatedTime'),
            'type' => $type,
            'timeInForce' => $this->safe_string_upper_2($order, 'force', 'timeInForce'),
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'triggerPrice' => $triggerPrice,
            'amount' => $totalAmount,
            'cost' => $cost,
            'average' => $avgPrice,
            'filled' => $filledAmount,
            'remaining' => $remaining,
            'status' => $this->parse_ws_order_status($rawStatus),
            'fee' => $feeObject,
            'trades' => null,
        ), $market);
    }

    public function parse_ws_order_status($status) {
        $statuses = array(
            'live' => 'open',
            'partially_filled' => 'open',
            'filled' => 'closed',
            'cancelled' => 'canceled',
            'not_trigger' => 'open',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function watch_my_trades(?string $symbol = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * watches $trades made by the user
             *
             * @see https://www.bitget.com/api-doc/contract/websocket/private/Fill-Channel
             * @see https://www.bitget.com/api-doc/uta/websocket/private/Fill-Channel
             *
             * @param {str} $symbol unified $market $symbol
             * @param {int} [$since] the earliest time in ms to fetch $trades for
             * @param {int} [$limit] the maximum number of $trades structures to retrieve
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {boolean} [$params->uta] set to true for the unified trading account ($uta), defaults to false
             * @return {array[]} a list of ~@link https://docs.ccxt.com/#/?id=trade-structure trade structures~
             */
            Async\await($this->load_markets());
            $market = null;
            $messageHash = 'myTrades';
            if ($symbol !== null) {
                $market = $this->market($symbol);
                $symbol = $market['symbol'];
                $messageHash = $messageHash . ':' . $symbol;
            }
            $type = null;
            list($type, $params) = $this->handle_market_type_and_params('watchMyTrades', $market, $params);
            $instType = null;
            $uta = null;
            list($uta, $params) = $this->handle_option_and_params($params, 'watchMyTrades', 'uta', false);
            if ($market === null && $type === 'spot') {
                $instType = 'SPOT';
            } else {
                list($instType, $params) = $this->get_inst_type($market, $uta, $params);
            }
            if ($uta) {
                $instType = 'UTA';
            }
            $subscriptionHash = 'fill:' . $instType;
            $args = array(
                'instType' => $instType,
            );
            $topicOrChannel = $uta ? 'topic' : 'channel';
            $args[$topicOrChannel] = 'fill';
            if (!$uta) {
                $args['instId'] = 'default';
            } else {
                $params = $this->extend($params, array( 'uta' => true ));
            }
            $trades = Async\await($this->watch_private($messageHash, $subscriptionHash, $args, $params));
            if ($this->newUpdates) {
                $limit = $trades->getLimit ($symbol, $limit);
            }
            return $this->filter_by_symbol_since_limit($trades, $symbol, $since, $limit, true);
        }) ();
    }

