        //       "close_avg_price":"0",
        //       "open_avg_price":"71.681666666666666667",
        //       "liquidate_price":"0",
        //       "create_time":1701621167225,
        //       "update_time":1701628152614
        //    }
        //
        $marketId = $this->safe_string($position, 'symbol');
        $market = $this->safe_market($marketId, $market, null, 'swap');
        $symbol = $market['symbol'];
        $openTimestamp = $this->safe_integer($position, 'create_time');
        $timestamp = $this->safe_integer($position, 'update_time');
        $side = $this->safe_number($position, 'position_type');
        $marginModeId = $this->safe_number($position, 'open_type');
        return $this->safe_position(array(
            'info' => $position,
            'id' => null,
            'symbol' => $symbol,
            'timestamp' => $openTimestamp,
            'datetime' => $this->iso8601($openTimestamp),
            'lastUpdateTimestamp' => $timestamp,
            'hedged' => null,
            'side' => ($side === 1) ? 'long' : 'short',
            'contracts' => $this->safe_number($position, 'hold_volume'),
            'contractSize' => $this->safe_number($market, 'contractSize'),
            'entryPrice' => $this->safe_number($position, 'open_avg_price'),
            'markPrice' => $this->safe_number($position, 'hold_avg_price'),
            'lastPrice' => null,
            'notional' => null,
            'leverage' => null,
            'collateral' => null,
            'initialMargin' => null,
            'initialMarginPercentage' => null,
            'maintenanceMargin' => null,
            'maintenanceMarginPercentage' => null,
            'unrealizedPnl' => null,
            'realizedPnl' => null,
            'liquidationPrice' => $this->safe_number($position, 'liquidate_price'),
            'marginMode' => ($marginModeId === 1) ? 'isolated' : 'cross',
            'percentage' => null,
            'marginRatio' => null,
            'stopLossPrice' => null,
            'takeProfitPrice' => null,
        ));
    }

    public function handle_trade(Client $client, $message) {
        //
        // spot
        //    {
        //        "table" => "spot/trade",
        //        "data" => array(
        //            array(
        //                "price" => "52700.50",
        //                "s_t" => 1630982050,
        //                "side" => "buy",
        //                "size" => "0.00112",
        //                "symbol" => "BTC_USDT"
        //            ),
        //        )
        //    }
        //
        // swap
        //    {
        //        "group":"futures/trade:BTCUSDT",
        //        "data":array(
        //           {
        //              "trade_id":6798697637,
        //              "contract_id":1,
        //              "symbol":"BTCUSDT",
        //              "deal_price":"39735.8",
        //              "deal_vol":"2",
        //              "type":0,
        //              "way":1,
        //              "create_time":1701618503,
        //              "create_time_mill":1701618503517,
        //              "created_at":"2023-12-03T15:48:23.517518538Z"
        //           }
        //        )
        //    }
        //
        $channel = $this->safe_string_2($message, 'table', 'group');
        $isSpot = (mb_strpos($channel, 'spot') !== false);
        $data = $this->safe_value($message, 'data');
        if ($data === null) {
            return;
        }
        $stored = null;
        for ($i = 0; $i < count($data); $i++) {
            $trade = $this->parse_ws_trade($data[$i]);
            $symbol = $trade['symbol'];
            $tradesLimit = $this->safe_integer($this->options, 'tradesLimit', 1000);
            $stored = $this->safe_value($this->trades, $symbol);
            if ($stored === null) {
                $stored = new ArrayCache ($tradesLimit);
                $this->trades[$symbol] = $stored;
            }
            $stored->append ($trade);
        }
        $messageHash = $channel;
        if ($isSpot) {
            $messageHash .= ':' . $this->safe_string($data[0], 'symbol');
        }
        $client->resolve ($stored, $messageHash);
        return $message;
    }

    public function parse_ws_trade($trade, ?array $market = null) {
        // spot
        //    {
        //        "price" => "52700.50",
        //        "s_t" => 1630982050,
        //        "side" => "buy",
        //        "size" => "0.00112",
        //        "symbol" => "BTC_USDT"
        //    }
        // swap
        //    {
        //       "trade_id":6798697637,
        //       "contract_id":1,
        //       "symbol":"BTCUSDT",
        //       "deal_price":"39735.8",
        //       "deal_vol":"2",
        //       "type":0,
        //       "way":1,
        //       "create_time":1701618503,
        //       "create_time_mill":1701618503517,
        //       "created_at":"2023-12-03T15:48:23.517518538Z"
        //    }
        //
        $contractId = $this->safe_string($trade, 'contract_id');
        $marketType = ($contractId === null) ? 'spot' : 'swap';
        $marketDelimiter = ($marketType === 'spot') ? '_' : '';
        $timestamp = $this->safe_integer($trade, 'create_time_mill', $this->safe_timestamp($trade, 's_t'));
        $marketId = $this->safe_string($trade, 'symbol');
        return $this->safe_trade(array(
            'info' => $trade,
            'id' => $this->safe_string($trade, 'trade_id'),
            'order' => null,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $this->safe_symbol($marketId, $market, $marketDelimiter, $marketType),
            'type' => null,
            'side' => $this->safe_string($trade, 'side'),
            'price' => $this->safe_string_2($trade, 'price', 'deal_price'),
            'amount' => $this->safe_string_2($trade, 'size', 'deal_vol'),
            'cost' => null,
            'takerOrMaker' => null,
            'fee' => null,
        ), $market);
    }

    public function handle_ticker(Client $client, $message) {
        //
        //    {
        //        "data" => array(
        //            {
        //                "base_volume_24h" => "78615593.81",
        //                "high_24h" => "52756.97",
        //                "last_price" => "52638.31",
        //                "low_24h" => "50991.35",
        //                "open_24h" => "51692.03",
        //                "s_t" => 1630981727,
        //                "symbol" => "BTC_USDT"
        //            }
        //        ),
        //        "table" => "spot/ticker"
        //    }
        //    {
        //        "group":"futures/ticker",
        //        "data":{
        //              "symbol":"BTCUSDT",
        //              "volume_24":"117387.58",
        //              "fair_price":"146.24",
        //              "last_price":"146.24",
        //              "range":"147.17",
        //              "ask_price" => "147.11",
        //              "ask_vol" => "1",
        //              "bid_price" => "142.11",
        //              "bid_vol" => "1"
        //            }
        //    }
        //
        $table = $this->safe_string($message, 'table');
        $isSpot = ($table !== null);
        $data = $this->safe_value($message, 'data');
        if ($data === null) {
            return;
        }
        if ($isSpot) {
            for ($i = 0; $i < count($data); $i++) {
                $ticker = $this->parse_ticker($data[$i]);
                $symbol = $ticker['symbol'];
                $marketId = $this->safe_string($ticker['info'], 'symbol');
                $messageHash = $table . ':' . $marketId;
                $this->tickers[$symbol] = $ticker;
                $client->resolve ($ticker, $messageHash);
                $this->resolve_message_hashes_for_symbol($client, $symbol, $ticker, 'tickers::');
            }
        } else {
            // on each update for contract markets, single $ticker is provided
            $ticker = $this->parse_ws_swap_ticker($data);
            $symbol = $this->safe_string($ticker, 'symbol');
            $this->tickers[$symbol] = $ticker;
            $client->resolve ($ticker, 'tickers::swap');
            $this->resolve_message_hashes_for_symbol($client, $symbol, $ticker, 'tickers::');
        }
        return $message;
    }

    public function resolve_message_hashes_for_symbol($client, $symbol, $result, $prexif) {
        $prefixSeparator = '::';
        $symbolsSeparator = ',';
        $messageHashes = $this->find_message_hashes($client, $prexif);
        for ($i = 0; $i < count($messageHashes); $i++) {
            $messageHash = $messageHashes[$i];
            $parts = explode($prefixSeparator, $messageHash);
            $length = count($parts);
            $symbolsString = $parts[$length - 1];
            $symbols = explode($symbolsSeparator, $symbolsString);
            if ($this->in_array($symbol, $symbols)) {
                $response = array();
                $response[$symbol] = $result;
                $client->resolve ($response, $messageHash);
            }
        }
    }

