        //         attributes => array( timestamp => 'sorted', $symbol => 'grouped' ),
        //         filter => array( $symbol => 'XBTUSD' ),
        //         data => array(
        //             {
        //                 timestamp => '2020-02-03T01:13:00.000Z',
        //                 $symbol => 'XBTUSD',
        //                 open => 9395,
        //                 high => 9395.5,
        //                 low => 9394.5,
