        //                 trades => 221,
        //                 volume => 839204,
        //                 vwap => 9394.9643,
        //                 lastSize => 1874,
        //                 turnover => 8932641535,
        //                 homeNotional => 89.32641534999999,
        //                 foreignNotional => 839204
        //             }
        //         )
        //     }
        //
        //
        //     {
        //         $table => 'tradeBin1m',
        //         action => 'insert',
        //         data => array(
        //             {
        //                 timestamp => '2020-02-03T18:28:00.000Z',
        //                 $symbol => 'XBTUSD',
        //                 open => 9256,
        //                 high => 9256.5,
        //                 low => 9256,
        //                 close => 9256,
