        // //  swap
        //     {
        //         action => 'snapshot',
        //         $arg => array( $instType => 'dmcbl', channel => 'account', instId => 'default' ),
        //         $data => array(
        //             {
        //                 marginCoin => 'ETH',
        //                 locked => '0.00000000',
