        //           "total" => "0.09986000000000000000",
        //           "available" => "0.09986000000000000000"
        //         }
        //       )
        //   }
        //
        // $account transfer
        //
        //    {
        //        "id" => null,
        //        "time" => 1653665088,
        //        "channel" => "futures.balances",
        //        "event" => "update",
        //        "error" => null,
