    }

    public function handle_trades(Client $client, $message) {
        //
        //     {
        //         "channel" => "trades",
        //         "data" => array(
        //             {
        //                 "coin" => "BTC",
        //                 "side" => "A",
        //                 "px" => "68517.0",
        //                 "sz" => "0.005",
        //                 "time" => 1710125266669,
        //                 "hash" => "0xc872699f116e012186620407fc08a802015e0097c5cce74710697f7272e6e959",
        //                 "tid" => 981894269203506
        //             }
        //         )
        //     }
        //
        $entry = $this->safe_list($message, 'data', array());
        $first = $this->safe_dict($entry, 0, array());
        $coin = $this->safe_string($first, 'coin');
        $marketId = $this->coinToMarketId ($coin);
        $market = $this->market($marketId);
        $symbol = $market['symbol'];
        if (!(is_array($this->trades) && array_key_exists($symbol, $this->trades))) {
            $limit = $this->safe_integer($this->options, 'tradesLimit', 1000);
            $stored = new ArrayCache ($limit);
            $this->trades[$symbol] = $stored;
        }
        $trades = $this->trades[$symbol];
        for ($i = 0; $i < count($entry); $i++) {
            $data = $this->safe_dict($entry, $i);
            $trade = $this->parse_ws_trade($data);
            $trades->append ($trade);
        }
        $messageHash = 'trade:' . $symbol;
        $client->resolve ($trades, $messageHash);
    }

    public function parse_ws_trade(array $trade, ?array $market = null): array {
        //
        // fetchMyTrades
        //
        //     {
        //         "coin" => "BTC",
        //         "px" => "72528.0",
        //         "sz" => "0.11693",
        //         "side" => "A",
        //         "time" => 1710208712815,
        //         "startPosition" => "0.11693",
        //         "dir" => "Close Long",
        //         "closedPnl" => "-0.81851",
        //         "hash" => "0xc5adaf35f8402750c218040b0a7bc301130051521273b6f398b3caad3e1f3f5f",
        //         "oid" => 7484888874,
        //         "crossed" => true,
        //         "fee" => "2.968244",
        //         "liquidationMarkPx" => null,
        //         "tid" => 567547935839686,
        //         "cloid" => null
        //     }
        //
        // fetchTrades
        //
        //     {
        //         "coin" => "BTC",
        //         "side" => "A",
        //         "px" => "68517.0",
        //         "sz" => "0.005",
        //         "time" => 1710125266669,
        //         "hash" => "0xc872699f116e012186620407fc08a802015e0097c5cce74710697f7272e6e959",
        //         "tid" => 981894269203506
        //     }
        //
        $timestamp = $this->safe_integer($trade, 'time');
        $price = $this->safe_string($trade, 'px');
        $amount = $this->safe_string($trade, 'sz');
        $coin = $this->safe_string($trade, 'coin');
        $marketId = $this->coinToMarketId ($coin);
        $market = $this->safe_market($marketId, null);
        $symbol = $market['symbol'];
        $id = $this->safe_string($trade, 'tid');
        $side = $this->safe_string($trade, 'side');
        if ($side !== null) {
            $side = ($side === 'A') ? 'sell' : 'buy';
        }
        $fee = $this->safe_string($trade, 'fee');
        return $this->safe_trade(array(
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'id' => $id,
            'order' => $this->safe_string($trade, 'oid'),
            'type' => null,
            'side' => $side,
            'takerOrMaker' => null,
            'price' => $price,
            'amount' => $amount,
            'cost' => null,
            'fee' => array( 'cost' => $fee, 'currency' => 'USDC' ),
        ), $market);
    }

    public function watch_ohlcv(string $symbol, string $timeframe = '1m', ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $timeframe, $since, $limit, $params) {
            /**
             * watches historical candlestick data containing the open, high, low, close price, and the volume of a $market
             *
             * @see https://hyperliquid.gitbook.io/hyperliquid-docs/for-developers/api/websocket/subscriptions
             *
             * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
             * @param {string} $timeframe the length of time each candle represents
             * @param {int} [$since] timestamp in ms of the earliest candle to fetch
             * @param {int} [$limit] the maximum amount of candles to fetch
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {int[][]} A list of candles ordered, open, high, low, close, volume
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $symbol = $market['symbol'];
            $url = $this->urls['api']['ws']['public'];
            $request = array(
                'method' => 'subscribe',
                'subscription' => array(
                    'type' => 'candle',
                    'coin' => $market['swap'] ? $market['baseName'] : $market['id'],
                    'interval' => $timeframe,
                ),
            );
            $messageHash = 'candles:' . $timeframe . ':' . $symbol;
            $message = $this->extend($request, $params);
            $ohlcv = Async\await($this->watch($url, $messageHash, $message, $messageHash));
            if ($this->newUpdates) {
                $limit = $ohlcv->getLimit ($symbol, $limit);
            }
            return $this->filter_by_since_limit($ohlcv, $since, $limit, 0, true);
        }) ();
    }

