    public function watch_orders(?string $symbol = null, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * watches information on multiple $orders made by the user
             *
             * @see https://hyperliquid.gitbook.io/hyperliquid-docs/for-developers/api/websocket/subscriptions
             *
             * @param {string} $symbol unified $market $symbol of the $market $orders were made in
             * @param {int} [$since] the earliest time in ms to fetch $orders for
             * @param {int} [$limit] the maximum number of order structures to retrieve
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {string} [$params->user] user address, will default to $this->walletAddress if not provided
             * @return {array[]} a list of ~@link https://docs.ccxt.com/?id=order-structure order structures~
             */
            Async\await($this->load_markets());
            $userAddress = null;
            list($userAddress, $params) = $this->handlePublicAddress ('watchOrders', $params);
            $market = null;
            $messageHash = 'order';
            if ($symbol !== null) {
                $market = $this->market($symbol);
                $symbol = $market['symbol'];
                $messageHash = $messageHash . ':' . $symbol;
            }
            $url = $this->urls['api']['ws']['public'];
            $request = array(
                'method' => 'subscribe',
                'subscription' => array(
                    'type' => 'orderUpdates',
                    'user' => $userAddress,
                ),
            );
            $message = $this->extend($request, $params);
            $orders = Async\await($this->watch($url, $messageHash, $message, $messageHash));
            if ($this->newUpdates) {
                $limit = $orders->getLimit ($symbol, $limit);
            }
            return $this->filter_by_symbol_since_limit($orders, $symbol, $since, $limit, true);
        }) ();
    }

    public function un_watch_orders(?string $symbol = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $params) {
            /**
             * unWatches information on multiple orders made by the user
             *
             * @see https://hyperliquid.gitbook.io/hyperliquid-docs/for-developers/api/websocket/subscriptions
             *
             * @param {string} $symbol unified market $symbol of the market orders were made in
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @param {string} [$params->user] user address, will default to $this->walletAddress if not provided
             * @return {array[]} a list of ~@link https://docs.ccxt.com/#/?id=order-structure order structures~
             */
            Async\await($this->load_markets());
            if ($symbol !== null) {
                throw new NotSupported($this->id . ' unWatchOrders() does not support a $symbol argument, unWatch from all markets only');
            }
            $messageHash = 'unsubscribe:order';
            $url = $this->urls['api']['ws']['public'];
            $userAddress = null;
            list($userAddress, $params) = $this->handlePublicAddress ('unWatchOrders', $params);
            $request = array(
                'method' => 'unsubscribe',
                'subscription' => array(
                    'type' => 'orderUpdates',
                    'user' => $userAddress,
                ),
            );
            $message = $this->extend($request, $params);
            return Async\await($this->watch($url, $messageHash, $message, $messageHash));
        }) ();
    }

    public function handle_order(Client $client, $message) {
        //
        //     {
        //         channel => 'orderUpdates',
        //         $data => array(
        //             {
        //                 $order => array(
        //                     coin => 'BTC',
        //                     side => 'B',
        //                     limitPx => '30000.0',
        //                     sz => '0.001',
        //                     oid => 7456484275,
        //                     timestamp => 1710163596492,
        //                     origSz => '0.001'
        //                 ),
        //                 status => 'open',
        //                 statusTimestamp => 1710163596492
        //             }
        //         )
        //     }
        //
        $data = $this->safe_list($message, 'data', array());
        if ($this->orders === null) {
            $limit = $this->safe_integer($this->options, 'ordersLimit', 1000);
            $this->orders = new ArrayCacheBySymbolById ($limit);
        }
        $dataLength = count($data);
        if ($dataLength === 0) {
            return;
        }
        $stored = $this->orders;
        $messageHash = 'order';
        $marketSymbols = array();
        for ($i = 0; $i < count($data); $i++) {
            $rawOrder = $data[$i];
            $order = $this->parse_order($rawOrder);
            $stored->append ($order);
            $symbol = $this->safe_string($order, 'symbol');
            $marketSymbols[$symbol] = true;
        }
        $keys = is_array($marketSymbols) ? array_keys($marketSymbols) : array();
        for ($i = 0; $i < count($keys); $i++) {
            $symbol = $keys[$i];
            $innerMessageHash = $messageHash . ':' . $symbol;
            $client->resolve ($stored, $innerMessageHash);
        }
        $client->resolve ($stored, $messageHash);
    }

    public function handle_error_message(Client $client, $message): Bool {
        //
        //    {
        //      "channel" => "post",
        //      "data" => {
        //        "id" => 1,
        //        "response" => {
        //          "type" => "action",
        //          "payload" => {
        //            "status" => "ok",
        //            "response" => {
        //              "type" => "order",
        //              "data" => {
        //                "statuses" => array(
        //                  {
        //                    "error" => "Order price cannot be more than 80% away from the reference price"
        //                  }
        //                )
        //              }
        //            }
        //          }
        //        }
        //      }
        //    }
        //
        //    {
        //         "channel" => "error",
        //         "data" => "Error parsing JSON into valid websocket request => array( \"type\" => \"allMids\" )"
        //     }
        //
        $channel = $this->safe_string($message, 'channel', '');
        if ($channel === 'error') {
            $ret_msg = $this->safe_string($message, 'data', '');
            $errorMsg = $this->id . ' ' . $ret_msg;
            $client->reject ($errorMsg);
            return true;
        }
        $data = $this->safe_dict($message, 'data', array());
        $id = $this->safe_string($message, 'id');
        if ($id === null) {
            $id = $this->safe_string($data, 'id');
        }
        $response = $this->safe_dict($data, 'response', array());
        $payload = $this->safe_dict($response, 'payload', array());
        $status = $this->safe_string($payload, 'status');
        if ($status !== null && $status !== 'ok') {
            $errorMsg = $this->id . ' ' . $this->json($payload);
            $client->reject ($errorMsg, $id);
            return true;
        }
        $type = $this->safe_string($payload, 'type');
        if ($type === 'error') {
            $error = $this->id . ' ' . $this->json($payload);
            $client->reject ($error, $id);
            return true;
        }
        try {
            $this->handle_errors(0, '', '', '', array(), $this->json($payload), $payload, array(), array());
        } catch (Exception $e) {
            $client->reject ($e, $id);
            return true;
        }
        return false;
    }

    public function handle_order_book_unsubscription(Client $client, array $subscription) {
        //
        //        "subscription":{
        //           "type":"l2Book",
        //           "coin":"BTC",
        //           "nSigFigs":5,
        //           "mantissa":null
        //        }
        //
        $coin = $this->safe_string($subscription, 'coin');
        $marketId = $this->coinToMarketId ($coin);
        $symbol = $this->safe_symbol($marketId);
        $subMessageHash = 'orderbook:' . $symbol;
        $messageHash = 'unsubscribe:' . $subMessageHash;
        $this->clean_unsubscription($client, $subMessageHash, $messageHash);
        if (is_array($this->orderbooks) && array_key_exists($symbol, $this->orderbooks)) {
            unset($this->orderbooks[$symbol]);
        }
    }

    public function handle_trades_unsubscription(Client $client, array $subscription) {
        //
        $coin = $this->safe_string($subscription, 'coin');
        $marketId = $this->coinToMarketId ($coin);
        $symbol = $this->safe_symbol($marketId);
        $subMessageHash = 'trade:' . $symbol;
        $messageHash = 'unsubscribe:' . $subMessageHash;
        $this->clean_unsubscription($client, $subMessageHash, $messageHash);
        if (is_array($this->trades) && array_key_exists($symbol, $this->trades)) {
            unset($this->trades[$symbol]);
        }
    }

    public function handle_tickers_unsubscription(Client $client, array $subscription) {
        //
        $subMessageHash = 'tickers';
        $messageHash = 'unsubscribe:' . $subMessageHash;
        $this->clean_unsubscription($client, $subMessageHash, $messageHash);
        $symbols = is_array($this->tickers) ? array_keys($this->tickers) : array();
        for ($i = 0; $i < count($symbols); $i++) {
            unset($this->tickers[$symbols[$i]]);
        }
    }

    public function handle_ohlcv_unsubscription(Client $client, array $subscription) {
        $coin = $this->safe_string($subscription, 'coin');
        $marketId = $this->coinToMarketId ($coin);
        $symbol = $this->safe_symbol($marketId);
        $interval = $this->safe_string($subscription, 'interval');
        $timeframe = $this->find_timeframe($interval);
        $subMessageHash = 'candles:' . $timeframe . ':' . $symbol;
        $messageHash = 'unsubscribe:' . $subMessageHash;
        $this->clean_unsubscription($client, $subMessageHash, $messageHash);
        if (is_array($this->ohlcvs) && array_key_exists($symbol, $this->ohlcvs)) {
            if (is_array($this->ohlcvs[$symbol]) && array_key_exists($timeframe, $this->ohlcvs[$symbol])) {
                unset($this->ohlcvs[$symbol][$timeframe]);
            }
        }
    }

    public function handle_order_unsubscription(Client $client, array $subscription) {
        $subHash = 'order';
        $unSubHash = 'unsubscribe:' . $subHash;
        $this->clean_unsubscription($client, $subHash, $unSubHash, true);
        $topicStructure = array(
            'topic' => 'orders',
        );
        $this->clean_cache($topicStructure);
    }

