    public function handle_trade(Client $client, $message) {
        //
        // snapshot
        //
        //    {
        //        "feed" => "trade_snapshot",
        //        "product_id" => "PI_XBTUSD",
        //        "trades" => array(
        //            array(
        //                "feed" => "trade",
        //                "product_id" => "PI_XBTUSD",
        //                "uid" => "caa9c653-420b-4c24-a9f1-462a054d86f1",
        //                "side" => "sell",
        //                "type" => "fill",
        //                "seq" => 655508,
        //                "time" => 1612269657781,
        //                "qty" => 440,
        //                "price" => 34893
        //            ),
        //            ...
        //        )
        //    }
        //
        // update
        //
        //    {
        //        "feed" => "trade",
        //        "product_id" => "PI_XBTUSD",
        //        "uid" => "05af78ac-a774-478c-a50c-8b9c234e071e",
        //        "side" => "sell",
        //        "type" => "fill",
        //        "seq" => 653355,
        //        "time" => 1612266317519,
        //        "qty" => 15000,
        //        "price" => 34969.5
        //    }
        //
        $channel = $this->safe_string($message, 'feed');
        $marketId = $this->safe_string_lower($message, 'product_id');
        if ($marketId !== null) {
            $market = $this->market($marketId);
            $symbol = $market['symbol'];
            $messageHash = 'trade:' . $symbol;
            $tradesArray = $this->safe_value($this->trades, $symbol);
            if ($tradesArray === null) {
                $tradesLimit = $this->safe_integer($this->options, 'tradesLimit', 1000);
                $tradesArray = new ArrayCache ($tradesLimit);
                $this->trades[$symbol] = $tradesArray;
            }
            if ($channel === 'trade_snapshot') {
                $trades = $this->safe_value($message, 'trades', array());
                for ($i = 0; $i < count($trades); $i++) {
                    $item = $trades[$i];
                    $trade = $this->parse_ws_trade($item);
                    $tradesArray->append ($trade);
                }
            } else {
                $trade = $this->parse_ws_trade($message);
                $tradesArray->append ($trade);
            }
            $client->resolve ($tradesArray, $messageHash);
        }
        return $message;
    }

