    public function parse_ws_ticker($ticker, $market = null) {
        //
        // spot
        //
        //     {
        //         "A" => "4.70432",
        //         "B" => "6.714863",
        //         "a" => "20744.54",
        //         "b" => "20744.17"
        //     }
